INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (307, 4, '115', 'Articolul 115', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre adun─ârile generale', NULL, NULL, '(1)...Pentru validitatea deliber─ârilor adun─ârii generale extraordinare este necesar─â la prima convocare prezen╚¢a ac╚¢ionarilor de╚¢in├ónd cel pu╚¢in o p─âtrime din num─ârul total de drepturi de vot, iar la convoc─ârile urm─âtoare, prezen╚¢a ac╚¢ionarilor reprezent├ónd cel pu╚¢in o cincime din num─ârul total de drepturi de vot....(2)...Hot─âr├órile sunt luate cu majoritatea voturilor de╚¢inute de ac╚¢ionarii prezen╚¢i sau reprezenta╚¢i. Decizia de modificare a obiectului principal de activitate al societ─â╚¢ii, de reducere sau majorare a capitalului social, de schimbare a formei juridice, de fuziune, divizare sau de dizolvare a societ─â╚¢ii se ia cu o majoritate de cel pu╚¢in dou─â treimi din drepturile de vot de╚¢inute de ac╚¢ionarii prezen╚¢i sau reprezenta╚¢i....(3)...├Än actul constitutiv se pot stipula cerin╚¢e de cvorum ╚Öi de majoritate mai mari.(la 01-12-2006, 
			Art. 115 a fost modificat de pct. 63 al art. I din LEGEA nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)', NULL, 128, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (308, 4, '116', 'Articolul 116', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre adun─ârile generale', NULL, NULL, '(1)...Hot─âr├órea unei adun─âri generale de a modifica drepturile sau obliga╚¢iile referitoare la o categorie de ac╚¢iuni nu produce efecte dec├ót ├«n urma aprob─ârii acestei hot─âr├óri de c─âtre adunarea special─â a de╚¢in─âtorilor de ac╚¢iuni din acea categorie....(2)...Dispozi╚¢iile prezentei sec╚¢iuni privind convocarea, cvorumul ╚Öi desf─â╚Öurarea adun─ârilor generale ale ac╚¢ionarilor se aplic─â ╚Öi adun─ârilor speciale....(3)...Hot─âr├órile ini╚¢iate de adun─ârile speciale vor fi supuse aprob─ârii adun─ârilor generale corespunz─âtoare.', NULL, 129, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (325, 4, '130', 'Articolul 130', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre adun─ârile generale', NULL, NULL, '(1)...Hot─âr├órile adun─ârilor generale se iau prin vot deschis....(2)...Votul secret este obligatoriu pentru numirea sau revocarea membrilor consiliului de administra╚¢ie, respectiv a membrilor consiliului de supraveghere, pentru numirea, revocarea ori demiterea cenzorilor sau auditorilor financiari ╚Öi pentru luarea hot─âr├órilor referitoare la r─âspunderea membrilor organelor de administrare, de conducere ╚Öi de control ale societ─â╚¢ii.(la 29-06-2007, 
			Alin. (2) al art. 130 a fost modificat de pct. 15 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
			)', NULL, 146, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (309, 4, '117', 'Articolul 117', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre adun─ârile generale', NULL, NULL, '(1)...Adunarea general─â este convocat─â de consiliul de administra╚¢ie, respectiv de directorat, ori de c├óte ori este necesar....(2)...Termenul de ├«ntrunire nu poate fi mai mic de 30 de zile de la publicarea convoc─ârii ├«n Monitorul Oficial al Rom├óniei, Partea a IV-a....(3)...Convocarea se public─â ├«n Monitorul Oficial al Rom├óniei, Partea a IV-a, ╚Öi ├«n unul dintre ziarele de larg─â r─âsp├óndire din localitatea ├«n care se afl─â sediul societ─â╚¢ii sau din cea mai apropiat─â localitate.(la 29-06-2007, 
			Alin. (3) al art. 117 a fost modificat de pct. 12 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
