INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (329, 4, '134', 'Articolul 134', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre adun─ârile generale', NULL, NULL, '(1)...Ac╚¢ionarii care nu au votat ├«n favoarea unei hot─âr├óri a adun─ârii generale au dreptul de a se retrage din societate ╚Öi de a solicita cump─ârarea ac╚¢iunilor lor de c─âtre societate, numai dac─â respectiva hot─âr├óre a adun─ârii generale are ca obiect:a)schimbarea obiectului principal de activitate;...b)transformarea transfrontalier─â a societ─â╚¢ii;(la 23-07-2023, 
