INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (333, 4, '137', 'Articolul 137', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...Societatea pe ac╚¢iuni este administrat─â de unul sau mai mul╚¢i administratori, num─ârul acestora fiind totdeauna impar. C├ónd sunt mai mul╚¢i administratori, ei constituie un consiliu de administra╚¢ie....(2)...Societ─â╚¢ile pe ac╚¢iuni ale c─âror situa╚¢ii financiare anuale fac obiectul unei obliga╚¢ii legale de auditare sunt administrate de cel pu╚¢in 3 administratori*)....(3)...Dispozi╚¢iile prezentei legi cu privire la consiliul de administra╚¢ie ╚Öi care nu privesc sau nu presupun pluralitatea administratorilor se aplic─â administratorului unic ├«n mod corespunz─âtor.(la 01-12-2006, 
