INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (334, 4, '137', 'Articolul 137', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...Administratorii sunt desemna╚¢i de c─âtre adunarea general─â ordinar─â a ac╚¢ionarilor, cu excep╚¢ia primilor administratori, care sunt numi╚¢i prin actul constitutiv....(2)...Candida╚¢ii pentru posturile de administrator sunt nominaliza╚¢i de c─âtre membrii actuali ai consiliului de administra╚¢ie sau de c─âtre ac╚¢ionari....(3)...Pe durata ├«ndeplinirii mandatului, administratorii nu pot ├«ncheia cu societatea un contract de munc─â. ├Än cazul ├«n care administratorii au fost desemna╚¢i dintre salaria╚¢ii societ─â╚¢ii, contractul individual de munc─â este suspendat pe perioada mandatului....(4)...Administratorii pot fi revoca╚¢i oric├ónd de c─âtre adunarea general─â ordinar─â a ac╚¢ionarilor. ├Än cazul ├«n care revocarea survine f─âr─â just─â cauz─â, administratorul este ├«ndrept─â╚¢it la plata unor daune-interese.(la 01-12-2006, 
