			)', NULL, 155, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (335, 4, '137', 'Articolul 137', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...├Än caz de vacan╚¢─â a unuia sau a mai multor posturi de administrator, dac─â prin actul constitutiv nu se dispune altfel, consiliul de administra╚¢ie procedeaz─â la numirea unor administratori provizorii, p├ón─â la ├«ntrunirea adun─ârii generale ordinare a ac╚¢ionarilor....(2)...Dac─â vacan╚¢a prev─âzut─â laalin. (1)determin─â sc─âderea num─ârului administratorilor sub minimul legal, administratorii r─âma╚Öi convoac─â de ├«ndat─â adunarea general─â ordinar─â a ac╚¢ionarilor, pentru a completa num─ârul de membri ai consiliului de administra╚¢ie....(3)...├Än cazul ├«n care administratorii nu ├«╚Öi ├«ndeplinesc obliga╚¢ia de a convoca adunarea general─â, orice parte interesat─â se poate adresa instan╚¢ei pentru a desemna persoana ├«ns─ârcinat─â cu convocarea adun─ârii generale ordinare a ac╚¢ionarilor, care s─â fac─â numirile necesare....(4)...C├ónd este un singur administrator ╚Öi acesta vrea s─â renun╚¢e la mandat, el va trebui s─â convoace adunarea general─â ordinar─â....(5)...├Än caz de deces sau de imposibilitate fizic─â de exercitare a func╚¢iei de administrator unic, numirea provizorie se va face de c─âtre cenzori, ├«ns─â adunarea general─â ordinar─â va fi convocat─â de urgen╚¢─â pentru numirea definitiv─â a administratorului....(6)...├Än cazul ├«n care societatea nu are cenzori, orice ac╚¢ionar se poate adresa instan╚¢ei care autorizeaz─â convocarea adun─ârii generale de c─âtre ac╚¢ionarul care a formulat cererea sau de c─âtre alt ac╚¢ionar. Prin aceea╚Öi hot─âr├óre, instan╚¢a