INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (338, 4, '138', 'Articolul 138', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...Prin actul constitutiv sau prin hot─âr├óre a adun─ârii generale a ac╚¢ionarilor se poate prevedea c─â unul sau mai mul╚¢i membri ai consiliului de administra╚¢ie trebuie s─â fie independen╚¢i....(2)...La desemnarea administratorului independent, adunarea general─â a ac╚¢ionarilor va avea ├«n vedere urm─âtoarele criterii:a)s─â nu fie director al societ─â╚¢ii sau al unei societ─â╚¢i controlate de c─âtre aceasta ╚Öi s─â nu fi ├«ndeplinit o astfel de func╚¢ie ├«n ultimii 5 ani;...b)s─â nu fi fost salariat al societ─â╚¢ii sau al unei societ─â╚¢i controlate de c─âtre aceasta ori s─â fi avut un astfel de raport de munc─â ├«n ultimii 5 ani;...c)s─â nu primeasc─â sau s─â fi primit de la societate ori de la o societate controlat─â de aceasta o remunera╚¢ie suplimentar─â sau alte avantaje, altele dec├ót cele corespunz├ónd calit─â╚¢ii sale de administrator neexecutiv;...d)s─â nu fie ac╚¢ionar semnificativ al societ─â╚¢ii;...e)s─â nu aib─â sau s─â fi avut ├«n ultimul an rela╚¢ii de afaceri cu societatea ori cu o societate controlat─â de aceasta, fie personal, fie ca asociat, ac╚¢ionar, administrator, director sau salariat al unei societ─â╚¢i care are astfel de rela╚¢ii cu societatea, dac─â, prin caracterul lor substan╚¢ial, acestea sunt de natur─â a-i afecta obiectivitatea;...f)s─â nu fie sau s─â fi fost ├«n ultimii 3 ani auditor financiar ori asociat salariat al actualului auditor financiar al societ─â╚¢ii sau al unei societ─â╚¢i controlate de aceasta;...g)s─â fie director ├«ntr-o alt─â societate ├«n care un director al societ─â╚¢ii este administrator neexecutiv;...h)s─â nu fi fost administrator neexecutiv al societ─â╚¢ii mai mult de 3 mandate;...i)s─â nu aib─â rela╚¢ii de familie cu o persoan─â aflat─â ├«n una dintre situa╚¢iile prev─âzute lalit. a)╚Öid)....(la 29-06-2007, 
			Alin. (2) al art. 138^2 a fost modificat de pct. 17 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
			)...(la 01-12-2006, 
