INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (345, 4, '142', 'Articolul 142', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...Consiliul de administra╚¢ie este ├«ns─ârcinat cu ├«ndeplinirea tuturor actelor necesare ╚Öi utile pentru realizarea obiectului de activitate al societ─â╚¢ii, cu excep╚¢ia celor rezervate de lege pentru adunarea general─â a ac╚¢ionarilor....(2)...Consiliul de administra╚¢ie are urm─âtoarele competen╚¢e de baz─â, care nu pot fi delegate directorilor:a)stabilirea direc╚¢iilor principale de activitate ╚Öi de dezvoltare ale societ─â╚¢ii;...b)stabilirea politicilor contabile ╚Öi a sistemului de control financiar, precum ╚Öi aprobarea planific─ârii financiare;(la 30-04-2008, 
