INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (364, 4, '153', 'Articolul 153', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...Conducerea societ─â╚¢ii pe ac╚¢iuni revine ├«n exclusivitate directoratului, care ├«ndepline╚Öte actele necesare ╚Öi utile pentru realizarea obiectului de activitate al societ─â╚¢ii, cu excep╚¢ia celor rezervate de lege ├«n sarcina consiliului de supraveghere ╚Öi a adun─ârii generale a ac╚¢ionarilor....(2)...Directoratul ├«╚Öi exercit─â atribu╚¢iile sub controlul consiliului de supraveghere....(3)...Directoratul este format din unul sau mai mul╚¢i membri, num─ârul acestora fiind totdeauna impar....(4)...C├ónd este un singur membru, acesta poart─â denumirea de director general unic. ├Än acest caz, dispozi╚¢iileart. 137 alin. (3)se aplic─â ├«n mod corespunz─âtor....(5)...├Än cazul societ─â╚¢ilor pe ac╚¢iuni ale c─âror situa╚¢ii financiare anuale fac obiectul unei obliga╚¢ii legale de auditare, directoratul este format din cel pu╚¢in 3 membri.(la 01-12-2006, 
