INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (365, 4, '153', 'Articolul 153', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...Desemnarea membrilor directoratului revine consiliului de supraveghere, care atribuie totodat─â unuia dintre ei func╚¢ia de pre╚Öedinte al directoratului....(2)...Actul constitutiv determin─â durata mandatului directoratului, ├«n limitele prev─âzute laart. 153^12....(3)...Membrii directoratului nu pot fi concomitent membri ai consiliului de supraveghere....(4)...Membrii directoratului pot fi revoca╚¢i oric├ónd de c─âtre consiliul de supraveghere. Actul constitutiv poate prevedea c─â ei pot fi revoca╚¢i ╚Öi de c─âtre adunarea general─â ordinar─â a ac╚¢ionarilor. Dac─â revocarea lor survine f─âr─â just─â cauz─â, membrii directoratului sunt ├«ndrept─â╚¢i╚¢i la plata unor daune-interese....(5)...├Än caz de vacan╚¢─â a unui post de membru al directoratului, consiliul de supraveghere va proceda f─âr─â ├«nt├órziere la desemnarea unui nou membru, pe durata r─âmas─â p├ón─â la expirarea mandatului directoratului....(6)...Cu privire la drepturile ╚Öi obliga╚¢iile membrilor directoratului,art. 137^1 alin. (3),art. 144^1,art. 144^2 alin. (1),(4)╚Öi(5),art. 144^3,art. 144^4,art. 150╚Öiart. 152se aplic─â ├«n mod corespunz─âtor.(la 29-06-2007, 
