			)', NULL, 186, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (366, 4, '153', 'Articolul 153', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...Directoratul reprezint─â societatea ├«n raport cu ter╚¢ii ╚Öi ├«n justi╚¢ie....(2)...├Än lipsa unei stipula╚¢ii contrare ├«n actul constitutiv, membrii directoratului reprezint─â societatea doar ac╚¢ion├ónd ├«mpreun─â....(3)...├Än situa╚¢ia ├«n care membrii directoratului reprezint─â societatea doar ac╚¢ion├ónd ├«mpreun─â, prin acordul lor unanim, ace╚Ötia ├«l pot ├«mputernici pe unul dintre ei s─â ├«ncheie anumite opera╚¢iuni sau tipuri de opera╚¢iuni....(4)...Consiliul de supraveghere reprezint─â societatea ├«n raporturile cu directoratul....(5)...Directoratul ├«nregistreaz─â la registrul comer╚¢ului numele membrilor s─âi, men╚¢ion├ónd dac─â ei ac╚¢ioneaz─â ├«mpreun─â sau separat.(la 26-11-2022, 
