INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (372, 4, '153', 'Articolul 153', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...Consiliul de supraveghere are urm─âtoarele atribu╚¢ii principale:a)exercit─â controlul permanent asupra conducerii societ─â╚¢ii de c─âtre directorat;...b)nume╚Öte ╚Öi revoc─â membrii directoratului;...c)verific─â conformitatea cu legea, cu actul constitutiv ╚Öi cu hot─âr├órile adun─ârii generale a opera╚¢iunilor de conducere a societ─â╚¢ii;...d)raporteaz─â cel pu╚¢in o dat─â pe an adun─ârii generale a ac╚¢ionarilor cu privire la activitatea de supraveghere desf─â╚Öurat─â.......(2)...├Än cazuri excep╚¢ionale, c├ónd interesul societ─â╚¢ii o cere, consiliul de supraveghere poate convoca adunarea general─â a ac╚¢ionarilor....(3)...Consiliului de supraveghere nu ├«i pot fi transferate atribu╚¢ii de conducere a societ─â╚¢ii. Cu toate acestea, ├«n actul constitutiv se poate prevedea c─â anumite tipuri de opera╚¢iuni nu pot fi efectuate dec├ót cu acordul consiliului. ├Än cazul ├«n care consiliul nu ├«╚Öi d─â acordul pentru o astfel de opera╚¢iune, directoratul poate cere acordul adun─ârii generale ordinare. Hot─âr├órea adun─ârii generale cu privire la un asemenea acord este dat─â cu o majoritate de 3 p─âtrimi din num─ârul voturilor ac╚¢ionarilor prezen╚¢i. Actul constitutiv nu poate stabili o alt─â majoritate ╚Öi nici stipula alte condi╚¢ii....(la 01-12-2006, 
