INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (375, 4, '153', 'Articolul 153', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...Durata mandatului administratorilor, respectiv al membrilor directoratului ╚Öi ai consiliului de supraveghere, este stabilit─â prin actul constitutiv, ea neput├ónd dep─â╚Öi 4 ani. Ei sunt reeligibili, c├ónd prin actul constitutiv nu se dispune altfel....(2)...Durata mandatului primilor membri ai consiliului de administra╚¢ie, respectiv al primilor membri ai consiliului de supraveghere, nu poate dep─â╚Öi 2 ani....(3)...Pentru ca numirea unui administrator, respectiv a unui membru al directoratului sau al consiliului de supraveghere, s─â fie valabil─â din punct de vedere juridic, persoana numit─â trebuie s─â o accepte ├«n mod expres....(4)...Persoana numit─â ├«n una dintre func╚¢iile prev─âzute laalin. (3)trebuie s─â fie asigurat─â pentru r─âspundere profesional─â.(la 29-06-2007, 
