INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (376, 4, '153', 'Articolul 153', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...Directorii societ─â╚¢ii pe ac╚¢iuni, ├«n sistemul unitar, respectiv membrii directoratului, ├«n sistemul dualist, sunt persoane fizice....(2)...O persoan─â juridic─â poate fi numit─â administrator sau membru al consiliului de supraveghere al unei societ─â╚¢i pe ac╚¢iuni. Odat─â cu aceast─â numire, persoana juridic─â este obligat─â s─â ├«╚Öi desemneze un reprezentant permanent, persoan─â fizic─â. Acesta este supus acelora╚Öi condi╚¢ii ╚Öi obliga╚¢ii ╚Öi are aceea╚Öi r─âspundere civil─â ╚Öi penal─â ca ╚Öi un administrator sau membru al consiliului de supraveghere, persoan─â fizic─â, ce ac╚¢ioneaz─â ├«n nume propriu, f─âr─â ca prin aceasta persoana juridic─â pe care o reprezint─â s─â fie exonerat─â de r─âspundere sau s─â i se mic╚Öoreze r─âspunderea solidar─â. C├ónd persoana juridic─â ├«╚Öi revoc─â reprezentantul, ea are obliga╚¢ia s─â numeasc─â ├«n acela╚Öi timp un ├«nlocuitor....(la 01-12-2006, 
