INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (378, 4, '153', 'Articolul 153', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, 'Directorii unei societ─â╚¢i pe ac╚¢iuni, ├«n sistemul unitar, ╚Öi membrii directoratului, ├«n sistemul dualist, nu vor putea fi, f─âr─â autorizarea consiliului de administra╚¢ie, respectiv a consiliului de supraveghere, directori, administratori, membri ai directoratului ori ai consiliului de supraveghere, cenzori sau, dup─â caz, auditori interni ori asocia╚¢i cu r─âspundere nelimitat─â, ├«n alte societ─â╚¢i concurente sau av├ónd acela╚Öi obiect de activitate, nici nu pot exercita acela╚Öi comer╚¢ sau altul concurent, pe cont propriu sau al altei persoane, sub pedeapsa revoc─ârii ╚Öi r─âspunderii pentru daune....(la 01-12-2006, 
			Art. 153^15 a fost introdus de pct. 107 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
