			)', NULL, 200, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (435, 4, '196', 'Articolul 196', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile cu r─âspundere limitat─â', NULL, NULL, NULL, NULL, 'Dispozi╚¢iile prev─âzute pentru societ─â╚¢ile pe ac╚¢iuni, ├«n ce prive╚Öte dreptul de a ataca hot─âr├órile adun─ârii generale, se aplic─â ╚Öi societ─â╚¢ilor cu r─âspundere limitat─â, termenul de 15 zile prev─âzut laart. 132 alin. (2)urm├ónd s─â curg─â de la data la care asociatul a luat cuno╚Ötin╚¢─â de hot─âr├órea adun─ârii generale pe care o atac─â.', NULL, 256, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (381, 4, '153', 'Articolul 153', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...Remunera╚¢ia membrilor consiliului de administra╚¢ie sau ai consiliului de supraveghere este stabilit─â prin actul constitutiv sau prin hot─âr├óre a adun─ârii generale a ac╚¢ionarilor....(2)...Remunera╚¢ia suplimentar─â a membrilor consiliului de administra╚¢ie sau ai consiliului de supraveghere ├«ns─ârcina╚¢i cu func╚¢ii specifice ├«n cadrul organului respectiv, precum ╚Öi remunera╚¢ia directorilor, ├«n sistemul unitar, ori a membrilor directoratului, ├«n sistemul dualist, sunt stabilite de consiliul de administra╚¢ie, respectiv de consiliul de supraveghere. Actul constitutiv sau adunarea general─â a ac╚¢ionarilor fixeaz─â limitele generale ale tuturor remunera╚¢iilor acordate ├«n acest fel....(3)...Orice alte avantaje pot fi acordate numai ├«n conformitate cualin. (1)╚Öi(2)....(4)...Adunarea general─â, respectiv consiliul de administra╚¢ie sau consiliul de supraveghere ╚Öi, dac─â este cazul, comitetul de remunerare se vor asigura, la stabilirea remunera╚¢iilor sau a altor avantaje, c─â acestea sunt justificate ├«n raport cu ├«ndatoririle specifice ale persoanelor respective ╚Öi cu situa╚¢ia economic─â a societ─â╚¢ii....(la 01-12-2006, 
