INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (382, 4, '153', 'Articolul 153', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, 'Consiliul de administra╚¢ie va solicita oficiului registrului comer╚¢ului ├«nregistrarea ├«n registrul comer╚¢ului a numirii directorilor, precum ╚Öi a oric─ârei schimb─âri ├«n persoana administratorilor sau directorilor. Aceste date se public─â ├«n Monitorul Oficial al Rom├óniei, Partea a IV-a. Aceea╚Öi obliga╚¢ie revine directoratului cu privire la ├«nregistrarea primilor membri ai directoratului ╚Öi a oric─ârei schimb─âri ├«n persoana membrilor directoratului sau a membrilor consiliului de supraveghere....(la 26-11-2022, 
			Articolul 153^19 din Subsectiunea a III-a , Sectiunea a III-a , Capitolul IV , Titlul III  a fost modificat de Punctul 38, Articolul 129, Sectiunea a 10-a, Capitolul IV din LEGEA nr. 265 din 22 iulie 2022, publicat─â ├«n MONITORUL OFICIAL nr. 750 din 26 iulie 2022
