INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (384, 4, '153', 'Articolul 153', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre administra╚¢ia societ─â╚¢ii', NULL, NULL, '(1)...Actul constitutiv poate dispune c─â, ├«n cazuri excep╚¢ionale, justificate prin urgen╚¢a situa╚¢iei ╚Öi prin interesul societ─â╚¢ii, deciziile consiliului de administra╚¢ie sau ale directoratului pot fi luate prin votul unanim exprimat ├«n scris al membrilor, f─âr─â a mai fi necesar─â o ├«ntrunire a respectivului organ....(2)...Nu se poate recurge la procedura prev─âzut─â laalin. (1)├«n cazul deciziilor consiliului de administra╚¢ie sau ale directoratului referitoare la situa╚¢iile financiare anuale ori la capitalul autorizat....(la 01-12-2006, 
