INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (399, 4, '163', 'Articolul 163', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Auditul financiar, auditul intern ╚Öi cenzorii', NULL, NULL, '(1)...Cenzorii sunt obliga╚¢i s─â supravegheze gestiunea societ─â╚¢ii, s─â verifice dac─â situa╚¢iile financiare sunt legal ├«ntocmite ╚Öi ├«n concordan╚¢─â cu registrele, dac─â acestea din urm─â sunt ╚¢inute regulat ╚Öi dac─â evaluarea elementelor patrimoniale s-a f─âcut conform regulilor stabilite pentru ├«ntocmirea ╚Öi prezentarea situa╚¢iilor financiare.(la 29-06-2007, 
			Alin. (1) al art. 163 a fost modificat de pct. 37 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
			)...(2)...Despre toate acestea, precum ╚Öi asupra propunerilor pe care le vor considera necesare cu privire la situa╚¢iile financiare ╚Öi repartizarea profitului, cenzorii vor prezenta adun─ârii generale un raport am─ânun╚¢it. Modalitatea ╚Öi procedura de raportare a auditorilor interni se stabilesc potrivit normelor elaborate de Camera Auditorilor Financiari din Rom├ónia.(la 29-06-2007, 
			Alin. (2) al art. 163 a fost modificat de pct. 37 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
			)...(3)...Adunarea general─â poate aproba situa╚¢iile financiare anuale numai dac─â acestea sunt ├«nso╚¢ite de raportul cenzorilor sau, dup─â caz, al auditorilor financiari....(4)...Abrogat.(la 01-12-2006, 
