INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (408, 4, '171', 'Articolul 171', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre emiterea de obliga╚¢iuni', NULL, NULL, '(1)...De╚¢in─âtorii de obliga╚¢iuni se pot ├«ntruni ├«n adunare general─â, pentru a delibera asupra intereselor lor....(2)...Adunarea va fi convocat─â pe cheltuiala societ─â╚¢ii care a emis obliga╚¢iunile, la cererea unui num─âr de de╚¢in─âtori care s─â reprezinte a patra parte din titlurile emise ╚Öi nerambursate sau, dup─â numirea reprezentan╚¢ilor de╚¢in─âtorilor de obliga╚¢iuni, la cererea acestora....(3)...Dispozi╚¢iile prev─âzute pentru adunarea ordinar─â a ac╚¢ionarilor se aplic─â ╚Öi adun─ârii de╚¢in─âtorilor de obliga╚¢iuni, ├«n ce prive╚Öte formele, condi╚¢iile, termenele convoc─ârii, depunerea titlurilor ╚Öi votarea....(4)...Societatea emitent─â nu poate participa la deliber─ârile adun─ârii de╚¢in─âtorilor de obliga╚¢iuni, ├«n baza obliga╚¢iunilor pe care le posed─â....(5)...De╚¢in─âtorii de obliga╚¢iuni vor putea fi reprezenta╚¢i prin mandatari, al╚¢ii dec├ót administratorii, directorii, respectiv membrii directoratului, ai consiliului de supraveghere ori cenzorii sau func╚¢ionarii societ─â╚¢ii.(la 01-12-2006, 
