INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (409, 4, '172', 'Articolul 172', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre emiterea de obliga╚¢iuni', NULL, NULL, '(1)...Adunarea de╚¢in─âtorilor de obliga╚¢iuni legal constituit─â poate:a)s─â numeasc─â un reprezentant al de╚¢in─âtorilor de obliga╚¢iuni ╚Öi unul sau mai mul╚¢i suplean╚¢i, cu dreptul de a-i reprezenta fa╚¢─â de societate ╚Öi ├«n justi╚¢ie, fix├óndu-le remunera╚¢ia; ace╚Ötia nu pot lua parte la administrarea societ─â╚¢ii, dar vor putea asista la adun─ârile sale generale;...b)s─â ├«ndeplineasc─â toate actele de supraveghere ╚Öi de ap─ârare a intereselor lor comune sau s─â autorizeze un reprezentant cu ├«ndeplinirea lor;...c)s─â constituie un fond, care va putea fi luat din dob├ónzile cuvenite de╚¢in─âtorilor de obliga╚¢iuni, pentru a face fa╚¢─â cheltuielilor necesare ap─âr─ârii drepturilor lor, stabilind, ├«n acela╚Öi timp, regulile pentru gestiunea acestui fond;...d)s─â se opun─â la orice modificare a actului constitutiv sau a condi╚¢iilor ├«mprumutului, prin care s-ar putea aduce o atingere drepturilor de╚¢in─âtorilor de obliga╚¢iuni;...e)s─â se pronun╚¢e asupra emiterii de noi obliga╚¢iuni.......(2)...Hot─âr├órile adun─ârii vor fi aduse la cuno╚Ötin╚¢─â societ─â╚¢ii, ├«n termen de cel mult 3 zile de la adoptarea lor.', NULL, 230, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (410, 4, '173', 'Articolul 173', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre emiterea de obliga╚¢iuni', NULL, NULL, 'Pentru validitatea deliber─ârilor prev─âzute laart. 172 alin. (1) lit. a),b)╚Öic)hot─âr├órea se ia cu o majoritate reprezent├ónd cel pu╚¢in o treime din titlurile emise ╚Öi nerambursate; ├«n celelalte cazuri este necesar─â prezen╚¢a ├«n adunare a de╚¢in─âtorilor reprezent├ónd cel pu╚¢in dou─â treimi din titlurile nerambursate ╚Öi votul favorabil a cel pu╚¢in patru cincimi din titlurile reprezentate la adunare.', NULL, 231, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (411, 4, '174', 'Articolul 174', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre emiterea de obliga╚¢iuni', NULL, NULL, '(1)...Hot─âr├órile luate de adunarea de╚¢in─âtorilor de obliga╚¢iuni sunt obligatorii ╚Öi pentru de╚¢in─âtorii care nu au luat parte la adunare sau au votat contra....(2)...Hot─âr├órile adun─ârii de╚¢in─âtorilor de obliga╚¢iuni pot fi atacate ├«n justi╚¢ie de c─âtre de╚¢in─âtorii care nu au luat parte la adunare sau au votat contra ╚Öi au cerut s─â se insereze aceasta ├«n procesul-verbal al ╚Öedin╚¢ei, ├«n termenul ╚Öi cu efectele ar─âtate ├«nart. 132╚Öi133.', NULL, 232, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (412, 4, '175', 'Articolul 175', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre emiterea de obliga╚¢iuni', NULL, NULL, 'Ac╚¢iunea ├«n justi╚¢ie a de╚¢in─âtorului de obliga╚¢iuni ├«mpotriva societ─â╚¢ii nu este admisibil─â dac─â are acela╚Öi obiect cu al ac╚¢iunii intentate de reprezentantul de╚¢in─âtorilor de obliga╚¢iuni sau este contrar─â unei hot─âr├óri a adun─ârii de╚¢in─âtorilor de obliga╚¢iuni.', NULL, 233, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (413, 4, '176', 'Articolul 176', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre emiterea de obliga╚¢iuni', NULL, NULL, '(1)...Obliga╚¢iunile se ramburseaz─â de societatea emitent─â la scaden╚¢─â....(2)...├Änainte de scaden╚¢─â, obliga╚¢iunile din aceea╚Öi emisiune ╚Öi cu aceea╚Öi valoare pot fi rambursate, prin tragere la sor╚¢i, la o sum─â superioar─â valorii lor nominale, stabilit─â de societate ╚Öi anun╚¢at─â public cu cel pu╚¢in 15 zile ├«nainte de data tragerii la sor╚¢i....(3)...Obliga╚¢iunile convertibile pot fi preschimbate ├«n ac╚¢iuni ale societ─â╚¢ii emitente, ├«n condi╚¢iile stabilite ├«n prospectul de ofert─â public─â.', NULL, 234, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
