INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (416, 4, '179', 'Articolul 179', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre registrele societ─â╚¢ii ╚Öi despre situa╚¢iile financiare anuale', NULL, NULL, 'Registrul ac╚¢ionarilor ╚Öi registrul obliga╚¢iunilor se pot ╚¢ine manual sau ├«n sistem computerizat.', NULL, 237, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
