INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (420, 4, '183', 'Articolul 183', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile pe ac╚¢iuni', NULL, 'Despre registrele societ─â╚¢ii ╚Öi despre situa╚¢iile financiare anuale', NULL, NULL, '(1)...Din profitul societ─â╚¢ii se va prelua, ├«n fiecare an, cel pu╚¢in 5% pentru formarea fondului de rezerv─â, p├ón─â ce acesta va atinge minimum a cincea parte din capitalul social....(2)...Dac─â fondul de rezerv─â, dup─â constituire, s-a mic╚Öorat din orice cauz─â, va fi completat, cu respectarea prevederiloralin. (1)....(3)...De asemenea, se include ├«n fondul de rezerv─â, chiar dac─â acesta a atins suma prev─âzut─â laalin. (1), excedentul ob╚¢inut prin v├ónzarea ac╚¢iunilor la un curs mai mare dec├ót valoarea lor nominal─â, dac─â acest excedent nu este ├«ntrebuin╚¢at la plata cheltuielilor de emisiune sau destinat amortiz─ârilor....(4)...Fondatorii vor participa la profit, dac─â acest lucru este prev─âzut ├«n actul constitutiv ori, ├«n lipsa unor asemenea prevederi, a fost aprobat de adunarea general─â extraordinar─â....(5)...├Än toate cazurile, condi╚¢iile particip─ârii se vor stabili de adunarea general─â, pentru fiecare exerci╚¢iu financiar.', NULL, 241, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
