INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (431, 4, '193', 'Articolul 193', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile cu r─âspundere limitat─â', NULL, NULL, NULL, NULL, '(1)...Fiecare parte social─â d─â dreptul la un vot....(2)...Un asociat nu poate exercita dreptul s─âu de vot ├«n deliber─ârile adun─ârilor asocia╚¢ilor referitoare la aporturile sale ├«n natur─â sau la actele juridice ├«ncheiate ├«ntre ele ╚Öi societate....(3)...Dac─â adunarea legal constituit─â nu poate lua o hot─âr├óre valabil─â din cauza ne├«ntrunirii majorit─â╚¢ii cerute, adunarea convocat─â din nou poate decide asupra ordinii de zi, oricare ar fi num─ârul de asocia╚¢i ╚Öi partea din capitalul social reprezentat─â de asocia╚¢ii prezen╚¢i.', NULL, 252, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (432, 4, '194', 'Articolul 194', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile cu r─âspundere limitat─â', NULL, NULL, NULL, NULL, '(1)...Adunarea general─â a asocia╚¢ilor are urm─âtoarele obliga╚¢ii principale:(la 01-12-2006, 
			Partea introductiv─â a alin. (1) al art. 194 a fost modificat─â de pct. 137 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)...a)...s─â aprobe situa╚¢ia financiar─â anual─â ╚Öi s─â stabileasc─â repartizarea profitului net;...b)...s─â desemneze administratorii ╚Öi cenzorii, s─â ├«i revoce/demit─â ╚Öi s─â le dea desc─ârcare de activitate, precum ╚Öi s─â decid─â contractarea auditului financiar, atunci c├ónd acesta nu are caracter obligatoriu, potrivit legii;(la 29-06-2007, 
			Lit. b) a alin. (1) al art. 194 a fost modificat─â de pct. 43 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
			)...c)...s─â decid─â urm─ârirea administratorilor ╚Öi cenzorilor pentru daunele pricinuite societ─â╚¢ii, desemn├ónd ╚Öi persoana ├«ns─ârcinat─â s─â o exercite;(la 29-06-2007, 
			Lit. c) a alin. (1) al art. 194 a fost modificat─â de pct. 43 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
			)...d)...s─â modifice actul constitutiv....(2)...├Än acest din urm─â caz, dac─â ├«n actul constitutiv se prevede dreptul de retragere a asociatului pentru c─â nu este de acord cu modific─ârile aduse acestuia, se aplic─â dispozi╚¢iileart. 224╚Öi225.', NULL, 253, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (433, 4, '195', 'Articolul 195', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile cu r─âspundere limitat─â', NULL, NULL, NULL, NULL, '(1)...Administratorii sunt obliga╚¢i s─â convoace adunarea asocia╚¢ilor la sediul social, cel pu╚¢in o dat─â pe an sau de c├óte ori este necesar....(2)...Un asociat sau un num─âr de asocia╚¢i, ce reprezint─â cel pu╚¢in o p─âtrime din capitalul social, va putea cere convocarea adun─ârii generale, ar─ât├ónd scopul acestei convoc─âri....(3)...Convocarea adun─ârii se va face ├«n forma prev─âzut─â ├«n actul constitutiv, iar ├«n lipsa unei dispozi╚¢ii speciale, prin scrisoare recomandat─â, cu cel pu╚¢in 10 zile ├«nainte de ziua fixat─â pentru ╚¢inerea acesteia, ar─ât├óndu-se ordinea de zi.', NULL, 254, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (434, 4, '195', 'Articolul 195', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile cu r─âspundere limitat─â', NULL, NULL, NULL, NULL, '(1)...Asocia╚¢ii, ├«n persoan─â sau reprezenta╚¢i, pot participa ╚Öi vota fizic sau prin mijloace electronice de comunicare la distan╚¢─â ├«n cadrul adun─ârilor generale potrivitalin. (2)....(2)...Dac─â actul constitutiv prevede sau dac─â asocia╚¢ii hot─âr─âsc astfel cu majoritatea prev─âzut─â deart. 192ori dac─â to╚¢i asocia╚¢ii convin astfel ├«n scris, asocia╚¢ii pot participa la adun─ârile generale prin mijloace electronice de comunicare la distan╚¢─â, care trebuie s─â ├«ntruneasc─â condi╚¢iile tehnice necesare pentru identificarea participan╚¢ilor, pentru participarea efectiv─â a acestora ╚Öi retransmiterea deliber─ârilor ├«n mod continuu, pentru exercitarea dreptului ac╚¢ionarilor de a se adresa adun─ârii, pentru exprimarea votului, ├«n cadrul adun─ârii generale, astfel ├«nc├ót s─â permit─â identificarea asocia╚¢ilor, verificarea ulterioar─â a modului ├«n care s-a votat ╚Öi posibilitatea ca fiecare ac╚¢ionar s─â ├«╚Öi poat─â verifica votul exprimat....(3)...Hot─âr├órile adoptate ├«n cadrul adun─ârii generale desf─â╚Öurate ├«n condi╚¢iilealin. (1)╚Öi(2)se semneaz─â olograf sau cu semn─âtur─â electronic─â, ├«n condi╚¢iile legii....(la 06-12-2024, 
			Capitolul VI , Titlul III  a fost completat de Punctul 12. , ARTICOLUL UNIC  din LEGEA nr. 299 din 2 decembrie 2024, publicat─â ├«n MONITORUL OFICIAL nr. 1212 din 3 decembrie 2024
			)', NULL, 255, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
