INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (436, 4, '196', 'Articolul 196', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile cu r─âspundere limitat─â', NULL, NULL, NULL, NULL, '(1)...├Än cazul societ─â╚¢ilor cu r─âspundere limitat─â cu asociat unic, acesta va exercita atribu╚¢iile adun─ârii generale a asocia╚¢ilor societ─â╚¢ii....(2)...Asociatul unic va consemna de ├«ndat─â, ├«n scris, orice decizie adoptat─â ├«n conformitate cualin. (1)....(3)...Asociatul unic poate avea calitatea de salariat al societ─â╚¢ii cu r─âspundere limitat─â al c─ârui asociat unic este.(la 29-06-2007, 
			Alin. (3) al art. 196^1 a fost modificat de pct. 44 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
			)...(4)...Eliminat.----------Alin. (4) al art. 196^1 a fost eliminat prin abrogarea pct. 45 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007 de c─âtre de pct. 6 al art. unic din LEGEA nr. 88 din 8 aprilie 2009, publicat─â ├«n MONITORUL OFICIAL nr. 246 din 14 aprilie 2009....(la 01-12-2006, 
