INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (437, 4, '197', 'Articolul 197', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile cu r─âspundere limitat─â', NULL, NULL, NULL, NULL, '(1)...Societatea este administrat─â de unul sau mai mul╚¢i administratori, asocia╚¢i sau neasocia╚¢i, numi╚¢i prin actul constitutiv sau de adunarea general─â....(2)...Administratorii nu pot primi, f─âr─â autorizarea adun─ârii asocia╚¢ilor, mandatul de administrator ├«n alte societ─â╚¢i concurente sau av├ónd acela╚Öi obiect de activitate, nici s─â fac─â acela╚Öi fel de comer╚¢ ori altul concurent pe cont propriu sau pe contul altei persoane fizice sau juridice, sub sanc╚¢iunea revoc─ârii ╚Öi r─âspunderii pentru daune....(3)...Dispozi╚¢iileart. 75,76,77 alin. (1)╚Öi79se aplic─â ╚Öi societ─â╚¢ilor cu r─âspundere limitat─â....(4)...Dispozi╚¢iile privitoare la administrarea societ─â╚¢ilor pe ac╚¢iuni nu sunt aplicabile societ─â╚¢ilor cu r─âspundere limitat─â, indiferent dac─â sunt sau nu supuse obliga╚¢iei de auditare.(la 17-04-2009, 
			Alin. (4) al art. 197 a fost introdus de pct. 7 al art. unic din LEGEA nr. 88 din 8 aprilie 2009, publicat─â ├«n MONITORUL OFICIAL nr. 246 din 14 aprilie 2009 care completeaz─â art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007, cu pct. 45^1.
			)', NULL, 258, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (438, 4, '198', 'Articolul 198', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile cu r─âspundere limitat─â', NULL, NULL, NULL, NULL, '(1)...Societatea trebuie s─â ╚¢in─â, prin grija administratorilor, un registru al asocia╚¢ilor, ├«n care se vor ├«nscrie, dup─â caz, numele ╚Öi prenumele, denumirea, domiciliul sau sediul fiec─ârui asociat, partea acestuia din capitalul social, transferul p─âr╚¢ilor sociale sau orice alt─â modificare privitoare la acestea....(2)...Administratorii r─âspund personal ╚Öi solidar pentru orice daun─â pricinuit─â prin nerespectarea prevederilor de laalin. (1)....(3)...Registrul poate fi cercetat de asocia╚¢i ╚Öi creditori.', NULL, 259, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
