INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (440, 4, '200', 'Articolul 200', NULL, 'Dispozi╚¢ii generale', NULL, 'Societ─â╚¢ile cu r─âspundere limitat─â', NULL, NULL, NULL, NULL, 'Societatea cu r─âspundere limitat─â nu poate emite obliga╚¢iuni.', NULL, 261, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
