INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (453, 4, '213', 'Articolul 213', NULL, 'Modificarea actului constitutiv', NULL, 'Reducerea sau majorarea capitalului social', NULL, NULL, NULL, NULL, 'Majorarea capitalului social al unei societ─â╚¢i prin ofert─â public─â de valori mobiliare ╚Öi/sau prin acordarea posibilit─â╚¢ii ac╚¢ionarilor de a-╚Öi tranzac╚¢iona drepturile de preferin╚¢─â pe pia╚¢a de capital este supus─â prevederilor legisla╚¢iei specifice pie╚¢ei de capital....(la 01-12-2006, 
