INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (454, 4, '214', 'Articolul 214', NULL, 'Modificarea actului constitutiv', NULL, 'Reducerea sau majorarea capitalului social', NULL, NULL, NULL, NULL, '├Än caz de majorare a capitalului social prin ofert─â public─â, administratorii, respectiv membrii directoratului, sunt solidar r─âspunz─âtori pentru exactitatea celor ar─âtate ├«n prospectul de emisiune, ├«n publica╚¢iile f─âcute de societate sau ├«n cererile adresate oficiului registrului comer╚¢ului, ├«n conformitate cu prevederile legisla╚¢iei privind pia╚¢a de capital....(la 29-06-2007, 
			Art. 214 a fost modificat de pct. 51 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
