INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (455, 4, '215', 'Articolul 215', NULL, 'Modificarea actului constitutiv', NULL, 'Reducerea sau majorarea capitalului social', NULL, NULL, NULL, NULL, '(1)...Dac─â majorarea capitalului social se face prin aporturi ├«n natur─â, adunarea general─â care a hot─âr├ót aceasta va propune registratorului de registrul comer╚¢ului numirea unuia sau a mai multor exper╚¢i pentru evaluarea acestor aporturi, ├«n condi╚¢iileart. 38╚Öi39.(la 26-11-2022, 
			Alineatul (1) din Articolul 215 , Capitolul II , Titlul IV  a fost modificat de Punctul 41, Articolul 129, Sectiunea a 10-a, Capitolul IV din LEGEA nr. 265 din 22 iulie 2022, publicat─â ├«n MONITORUL OFICIAL nr. 750 din 26 iulie 2022
			)...(1^1)...├Än cazul ├«n care majorarea capitalului social este efectuat─â pentru realizarea unei fuziuni sau diviz─âri ╚Öi pentru efectuarea, dac─â este cazul, a pl─â╚¢ilor ├«n numerar c─âtre ac╚¢ionarii/asocia╚¢ii societ─â╚¢ii absorbite sau divizate, nu este necesar─â ├«ntocmirea raportului prev─âzut laalin. (1), dac─â proiectul de fuziune sau divizare a fost supus examin─ârii unui expert independent potrivit dispozi╚¢iilorart. 243^3 alin. (1)-(4).(la 02-06-2012, 
			Alin. (1^1) al art. 215 a fost modificat de pct. 31 al art. 18, Titlul IV din LEGEA nr. 76 din 24 mai 2012, publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012, prin ├«nlocuirea sintagmei "societ─â╚¢i comerciale" cu termenul "societ─â╚¢i".
			)...(2)...Aporturi ├«n crean╚¢e nu sunt admise....(3)...Dup─â depunerea raportului de expertiz─â, adunarea general─â extraordinar─â convocat─â din nou, av├ónd ├«n vedere concluziile exper╚¢ilor, poate hot─âr├« majorarea capitalului social....(4)...Hot─âr├órea adun─ârii generale trebuie s─â cuprind─â descrierea aporturilor ├«n natur─â, numele persoanelor ce le efectueaz─â ╚Öi num─ârul ac╚¢iunilor ce se vor emite ├«n schimb.', NULL, 276, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (456, 4, '216', 'Articolul 216', NULL, 'Modificarea actului constitutiv', NULL, 'Reducerea sau majorarea capitalului social', NULL, NULL, NULL, NULL, '(1)...Ac╚¢iunile emise pentru majorarea capitalului social vor fi oferite spre subscriere, ├«n primul r├ónd ac╚¢ionarilor existen╚¢i, propor╚¢ional cu num─ârul ac╚¢iunilor pe care le posed─â....(2)...Exercitarea dreptului de preferin╚¢─â se va putea realiza numai ├«n interiorul termenului hot─âr├ót de adunarea general─â sau de consiliul de administra╚¢ie, respectiv directorat, ├«n condi╚¢iileart. 220^1 alin. (4), dac─â actul constitutiv nu prevede alt termen. ├Än toate situa╚¢iile, termenul acordat pentru exercitarea drepturilor de preferin╚¢─â nu poate fi mai mic de o lun─â de la data public─ârii hot─âr├órii adun─ârii generale, respectiv a deciziei consiliului de administra╚¢ie/directoratului, ├«n Monitorul Oficial al Rom├óniei, Partea a IV-a. Dup─â expirarea acestui termen, ac╚¢iunile vor putea fi oferite spre subscriere publicului.(la 29-06-2007, 
			Alin. (2) al art. 216 a fost modificat de pct. 52 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
			)...(3)...Orice majorare a capitalului social efectuat─â cu ├«nc─âlcarea prezentului articol este anulabil─â.(la 01-12-2006, 
			Art. 216 a fost modificat de pct. 148 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)', NULL, 277, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (457, 4, '216', 'Articolul 216', NULL, 'Modificarea actului constitutiv', NULL, 'Reducerea sau majorarea capitalului social', NULL, NULL, NULL, NULL, 'Ac╚¢ionarii au un drept de preferin╚¢─â ╚Öi atunci c├ónd societatea emite obliga╚¢iuni convertibile ├«n ac╚¢iuni. Dispozi╚¢iileart. 216se aplic─â ├«n mod corespunz─âtor.(la 01-12-2006, 
