INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (458, 4, '217', 'Articolul 217', NULL, 'Modificarea actului constitutiv', NULL, 'Reducerea sau majorarea capitalului social', NULL, NULL, NULL, NULL, '(1)...Dreptul de preferin╚¢─â al ac╚¢ionarilor poate fi limitat sau ridicat numai prin hot─âr├órea adun─ârii generale extraordinare a ac╚¢ionarilor....(2)...Consiliul de administra╚¢ie, respectiv directoratul, va pune la dispozi╚¢ie adun─ârii generale extraordinare a ac╚¢ionarilor un raport scris, prin care se precizeaz─â motivele limit─ârii sau ridic─ârii dreptului de preferin╚¢─â. Acest raport va explica, de asemenea, modul de determinare a valorii de emisiune a ac╚¢iunilor....(3)...Hot─âr├órea va fi luat─â ├«n prezen╚¢a ac╚¢ionarilor reprezent├ónd trei p─âtrimi din capitalul social subscris, cu majoritatea voturilor ac╚¢ionarilor prezen╚¢i....(4)...Hot─âr├órea va fi depus─â la oficiul registrului comer╚¢ului de c─âtre consiliul de administra╚¢ie, respectiv de c─âtre directorat, spre men╚¢ionare ├«n registrul comer╚¢ului ╚Öi publicare ├«n Monitorul Oficial al Rom├óniei, Partea a IV-a.(la 01-12-2006, 
