INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (461, 4, '220', 'Articolul 220', NULL, 'Modificarea actului constitutiv', NULL, 'Reducerea sau majorarea capitalului social', NULL, NULL, NULL, NULL, '(1)...Ac╚¢iunile emise ├«n schimbul aporturilor ├«n numerar vor trebui pl─âtite, la data subscrierii, ├«n propor╚¢ie de cel pu╚¢in 30% din valoarea lor nominal─â ╚Öi, integral, ├«n termen de cel mult 3 ani de la data public─ârii ├«n Monitorul Oficial al Rom├óniei, Partea a IV-a, a hot─âr├órii adun─ârii generale....(2)...├Än acela╚Öi termen vor trebui pl─âtite ac╚¢iunile emise ├«n schimbul aporturilor ├«n natur─â....(3)...C├ónd s-a prev─âzut o prim─â de emisiune, aceasta trebuie integral pl─âtit─â la data subscrierii....(4)...Dispozi╚¢iileart. 98 alin. (3)╚Öi aleart. 100r─âm├ón aplicabile.', NULL, 282, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (462, 4, '220', 'Articolul 220', NULL, 'Modificarea actului constitutiv', NULL, 'Reducerea sau majorarea capitalului social', NULL, NULL, NULL, NULL, '(1)...Prin actul constitutiv, consiliul de administra╚¢ie, respectiv directoratul, poate fi autorizat ca, ├«ntr-o anumit─â perioad─â, ce nu poate dep─â╚Öi 5 ani de la data ├«nmatricul─ârii societ─â╚¢ii, s─â majoreze capitalul social subscris p├ón─â la o valoare nominal─â determinat─â (capital autorizat), prin emiterea de noi ac╚¢iuni ├«n schimbul aporturilor....(2)...O astfel de autorizare poate fi acordat─â ╚Öi de adunarea general─â a ac╚¢ionarilor, printr-o modificare a actului constitutiv, pentru o anumit─â perioad─â, ce nu poate dep─â╚Öi 5 ani de la data ├«nregistr─ârii modific─ârii. Actul constitutiv poate majora cerin╚¢ele de cvorum pentru o astfel de modificare....(3)...Valoarea nominal─â a capitalului autorizat nu poate dep─â╚Öi jum─âtate din capitalul social subscris, existent ├«n momentul autoriz─ârii....(4)...Prin autorizarea acordat─â conformalin. (1)-(3), consiliului de administra╚¢ie, respectiv directoratului, ├«i poate fi conferit─â ╚Öi competen╚¢a de a decide restr├óngerea sau ridicarea dreptului de preferin╚¢─â al ac╚¢ionarilor existen╚¢i. Aceast─â autorizare se acord─â consiliului de administra╚¢ie, respectiv directoratului, de c─âtre adunarea general─â, ├«n condi╚¢iile de cvorum ╚Öi majoritate prev─âzute laart. 217 alin. (3). Decizia consiliului de administra╚¢ie, respectiv a directoratului, cu privire la restr├óngerea sau ridicarea dreptului de preferin╚¢─â se depune la oficiul registrului comer╚¢ului, spre men╚¢ionare ├«n registrul comer╚¢ului ╚Öi publicare ├«n Monitorul Oficial al Rom├óniei, Partea a IV-a.(la 29-06-2007, 
