INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (463, 4, '221', 'Articolul 221', NULL, 'Modificarea actului constitutiv', NULL, 'Reducerea sau majorarea capitalului social', NULL, NULL, NULL, NULL, 'Societatea cu r─âspundere limitat─â ├«╚Öi poate majora capitalul social, ├«n modalit─â╚¢ile ╚Öi din sursele prev─âzute deart. 210....(la 29-06-2007, 
