			)', NULL, 284, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (464, 4, '222', 'Articolul 222', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Poate fi exclus din societatea ├«n nume colectiv, ├«n comandit─â simpl─â sau cu r─âspundere limitat─â:a)asociatul care, pus ├«n ├«nt├órziere, nu aduce aportul la care s-a obligat;...b)asociatul cu r─âspundere nelimitat─â ├«n stare de faliment sau care a devenit legalmente incapabil;...c)asociatul cu r─âspundere nelimitat─â care se amestec─â f─âr─â drept ├«n administra╚¢ie ori contravine dispozi╚¢iilorart. 80╚Öi82;...d)asociatul administrator care comite fraud─â ├«n dauna societ─â╚¢ii sau se serve╚Öte de semn─âtura social─â sau de capitalul social ├«n folosul lui sau al altora.......(2)...Dispozi╚¢iile acestui articol se aplic─â ╚Öi comandita╚¢ilor ├«n societatea ├«n comandit─â pe ac╚¢iuni.', NULL, 285, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (465, 4, '223', 'Articolul 223', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Excluderea se pronun╚¢─â prin hot─âr├óre judec─âtoreasc─â la cererea societ─â╚¢ii sau a oric─ârui asociat....(2)...C├ónd excluderea se cere de c─âtre un asociat, se vor cita societatea ╚Öi asociatul p├ór├ót....(3)...Ca urmare a excluderii, instan╚¢a judec─âtoreasc─â va dispune, prin aceea╚Öi hot─âr├óre, ╚Öi cu privire la structura particip─ârii la capitalul social a celorlal╚¢i asocia╚¢i....(3^1)...Hot─âr├órea prin care instan╚¢a se pronun╚¢─â asupra cererii de excludere este supus─â numai apelului.(la 02-06-2012, 
			Alin. (3^1) al art. 223 a fost introdus de pct. 19 al art. 18, Titlul IV din LEGEA nr. 76 din 24 mai 2012, publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012.
			)...(4)...Hot─âr├órea definitiv─â de excludere se depune, ├«n termen de 15 zile, la oficiul registrului comer╚¢ului pentru a fi ├«nscris─â ├«n registru, iar dispozitivul hot─âr├órii se public─â, la cererea societ─â╚¢ii, ├«n Monitorul Oficial al Rom├óniei, Partea a IV-a.(la 02-06-2012, 
			Alin. (4) al art. 223 a fost modificat de pct. 20 al art. 18, Titlul IV din LEGEA nr. 76 din 24 mai 2012, publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012.
			)', NULL, 286, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (466, 4, '224', 'Articolul 224', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Asociatul exclus r─âspunde de pierderi ╚Öi are dreptul la beneficii p├ón─â ├«n ziua excluderii sale, ├«ns─â nu va putea cere lichidarea lor p├ón─â ce acestea nu sunt repartizate conform prevederilor actului constitutiv....(2)...Asociatul exclus nu are dreptul la o parte propor╚¢ional─â din patrimoniul social, ci numai la o sum─â de bani care s─â reprezinte valoarea acesteia.', NULL, 287, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (467, 4, '225', 'Articolul 225', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Asociatul exclus r─âm├óne obligat fa╚¢─â de ter╚¢i pentru opera╚¢iunile f─âcute de societate, p├ón─â ├«n ziua r─âm├ónerii definitive a hot─âr├órii de excludere....(2)...Dac─â, ├«n momentul excluderii, sunt opera╚¢iuni ├«n curs de executare, asociatul este obligat s─â suporte consecin╚¢ele ╚Öi nu-╚Öi va putea retrage partea ce i se cuvine dec├ót dup─â terminarea acelor opera╚¢iuni.', NULL, 288, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (468, 4, '226', 'Articolul 226', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Asociatul ├«n societatea ├«n nume colectiv, ├«n comandit─â simpl─â sau ├«n societatea cu r─âspundere limitat─â se poate retrage din societate:a)├«n cazurile prev─âzute ├«n actul constitutiv;...a^1)├«n cazurile prev─âzute laart. 134;(la 02-03-2012, 
