INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (469, 4, '227', 'Articolul 227', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Dizolvarea societ─â╚¢ilor', NULL, NULL, NULL, NULL, '(1)...Societatea se dizolv─â prin:a)trecerea timpului stabilit pentru durata societ─â╚¢ii;...b)imposibilitatea realiz─ârii obiectului de activitate al societ─â╚¢ii sau realizarea acestuia;...c)declararea nulit─â╚¢ii societ─â╚¢ii;...d)hot─âr├órea adun─ârii generale;...e)hot─âr├órea tribunalului, la cererea oric─ârui asociat, pentru motive temeinice, precum ne├«n╚¢elegerile grave dintre asocia╚¢i, care ├«mpiedic─â func╚¢ionarea societ─â╚¢ii;...f)falimentul societ─â╚¢ii;...g)alte cauze prev─âzute de lege sau de actul constitutiv al societ─â╚¢ii.......(2)...├Än cazul prev─âzut laalin. (1) lit. a), asocia╚¢ii trebuie s─â fie consulta╚¢i de c─âtre consiliul de administra╚¢ie, respectiv de directorat, cu cel pu╚¢in 3 luni ├«nainte de expirarea duratei societ─â╚¢ii, cu privire la eventuala prelungire a acesteia. ├Än lips─â, la cererea oric─âruia dintre asocia╚¢i, tribunalul poate dispune, prin ├«ncheiere, efectuarea consult─ârii conformart. 119 alin. (3).(la 01-12-2006, 
			Alin. (2) al art. 227 a fost modificat de pct. 154 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)...(3)...Abrogat.(la 26-11-2022, 
