INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (470, 4, '228', 'Articolul 228', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Dizolvarea societ─â╚¢ilor', NULL, NULL, NULL, NULL, '(1)...Societatea pe ac╚¢iuni se dizolv─â:a)├«n cazul ╚Öi ├«n condi╚¢iile prev─âzute laart. 153^24;...b)├«n cazul ╚Öi ├«n condi╚¢iile prev─âzute laart. 10 alin. (3).......(2)...Prevederilealin. (1) lit. a)se aplic─â ├«n mod corespunz─âtor ╚Öi societ─â╚¢ii cu r─âspundere limitat─â.(la 29-06-2007, 
