INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (471, 4, '229', 'Articolul 229', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Dizolvarea societ─â╚¢ilor', NULL, NULL, NULL, NULL, '(1)...Societ─â╚¢ile ├«n nume colectiv sau cu r─âspundere limitat─â se dizolv─â prin falimentul, incapacitatea, excluderea, retragerea sau decesul unuia dintre asocia╚¢i, c├ónd, datorit─â acestor cauze, num─ârul asocia╚¢ilor s-a redus la unul singur....(2)...Se excepteaz─â cazul c├ónd ├«n actul constitutiv exist─â clauz─â de continuare cu mo╚Ötenitorii sau c├ónd asociatul r─âmas hot─âr─â╚Öte continuarea existen╚¢ei societ─â╚¢ii sub forma societ─â╚¢ii cu r─âspundere limitat─â cu asociat unic....(3)...Dispozi╚¢iile alineatelor precedente se aplic─â ╚Öi societ─â╚¢ilor ├«n comandit─â simpl─â sau ├«n comandit─â pe ac╚¢iuni, dac─â acele cauze privesc pe singurul asociat comanditat sau comanditar.', NULL, 292, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (472, 4, '230', 'Articolul 230', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Dizolvarea societ─â╚¢ilor', NULL, NULL, NULL, NULL, '(1)...├Än societ─â╚¢ile ├«n nume colectiv, societ─â╚¢ile cu r─âspundere limitat─â, dac─â un asociat decedeaz─â ╚Öi dac─â nu exist─â conven╚¢ie contrar─â, societatea trebuie s─â pl─âteasc─â partea ce se cuvine mo╚Ötenitorilor, dup─â ultimul bilan╚¢ contabil aprobat, ├«n termen de 3 luni de la notificarea decesului asociatului, dac─â asocia╚¢ii r─âma╚Öi nu prefer─â s─â continue societatea cu mo╚Ötenitorii care consimt la aceasta.(la 26-11-2022, 
			Alineatul (1) din Articolul 230 , Capitolul I , Titlul VI  a fost modificat de Punctul 45, Articolul 129, Sectiunea a 10-a, Capitolul IV din LEGEA nr. 265 din 22 iulie 2022, publicat─â ├«n MONITORUL OFICIAL nr. 750 din 26 iulie 2022
			)...(2)...Prevederilealin. (1)se aplic─â ╚Öi societ─â╚¢ilor ├«n comandit─â simpl─â, ├«n caz de deces al unuia dintre asocia╚¢ii comandita╚¢i, ├«n afar─â de cazul c├ónd mo╚Ötenitorii s─âi nu prefer─â s─â r─âm├ón─â ├«n societate ├«n aceast─â calitate....(3)...Mo╚Ötenitorii r─âm├ón r─âspunz─âtori, potrivitart. 224, p├ón─â la publicarea schimb─ârilor intervenite.', NULL, 293, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (473, 4, '231', 'Articolul 231', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Dizolvarea societ─â╚¢ilor', NULL, NULL, NULL, NULL, '(1)...├Än caz de dizolvare a societ─â╚¢ii prin hot─âr├óre a asocia╚¢ilor, ace╚Ötia vor putea reveni, cu majoritatea cerut─â pentru modificarea actului constitutiv, asupra hot─âr├órii luate, at├ót timp c├ót nu s-a f─âcut nici o reparti╚¢ie din activ....(2)...Noua hot─âr├óre se men╚¢ioneaz─â ├«n registrul comer╚¢ului, dup─â care oficiul registrului comer╚¢ului o va trimite Monitorului Oficial al Rom├óniei, spre publicare ├«n Partea a IV-a, pe cheltuiala societ─â╚¢ii....(3)...Creditorii ╚Öi orice parte interesat─â pot face opozi╚¢ie la tribunal ├«mpotriva hot─âr├órii, ├«n condi╚¢iileart. 62.', NULL, 294, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (474, 4, '232', 'Articolul 232', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Dizolvarea societ─â╚¢ilor', NULL, NULL, NULL, NULL, '(1)...Dizolvarea societ─â╚¢ilor trebuie s─â fie ├«nscris─â ├«n registrul comer╚¢ului ╚Öi publicat─â ├«n Monitorul Oficial al Rom├óniei, Partea a IV-a, ├«n afar─â de cazul prev─âzut laart. 227 alin. (1) lit. a).(la 02-06-2012, 
			Alin. (1) al art. 232 a fost modificat de pct. 31 al art. 18, Titlul IV din LEGEA nr. 76 din 24 mai 2012, publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012, prin ├«nlocuirea sintagmei "societ─â╚¢i comerciale" cu termenul "societ─â╚¢i".
			)...(2)...├Änscrierea ╚Öi publicarea se vor face conformart. 204, c├ónd dizolvarea are loc ├«n baza unei hot─âr├óri a adun─ârii generale, ╚Öi ├«n termen de 15 zile de la data la care hot─âr├órea judec─âtoreasc─â a r─âmas definitiv─â, c├ónd dizolvarea a fost pronun╚¢at─â de justi╚¢ie.(la 02-06-2012, 
			Alin. (2) al art. 232 a fost modificat de pct. 22 al art. 18, Titlul IV din LEGEA nr. 76 din 24 mai 2012, publicat─â ├«n MONITORUL OFICIAL nr. 365 din 30 mai 2012.
			)...(3)...├Än cazul prev─âzut laart. 227 alin. (1) lit. f), dizolvarea se pronun╚¢─â de tribunalul ├«nvestit cu procedura falimentului.', NULL, 295, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (475, 4, '233', 'Articolul 233', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Dizolvarea societ─â╚¢ilor', NULL, NULL, NULL, NULL, '(1)...Dizolvarea societ─â╚¢ii are ca efect deschiderea procedurii lichid─ârii. Dizolvarea are loc f─âr─â lichidare, ├«n cazul fuziunii ori diviz─ârii totale a societ─â╚¢ii sau ├«n alte cazuri prev─âzute de lege....(2)...Din momentul dizolv─ârii, directorii, administratorii, respectiv directoratul, nu mai pot ├«ntreprinde noi opera╚¢iuni. ├Än caz contrar, ace╚Ötia sunt personal ╚Öi solidar r─âspunz─âtori pentru ac╚¢iunile ├«ntreprinse.(la 01-12-2006, 
			Alin. (2) al art. 233 a fost modificat de pct. 156 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)...(3)...Interdic╚¢ia prev─âzut─â laalin. (2)se aplic─â din ziua expir─ârii termenului fixat pentru durata societ─â╚¢ii ori de la data la care dizolvarea a fost hot─âr├ót─â de adunarea general─â sau declarat─â prin sentin╚¢─â judec─âtoreasc─â....(4)...Societatea ├«╚Öi p─âstreaz─â personalitatea juridic─â pentru opera╚¢iunile lichid─ârii, p├ón─â la terminarea acesteia.', NULL, 296, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (477, 4, '235', 'Articolul 235', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Dizolvarea societ─â╚¢ilor', NULL, NULL, NULL, NULL, '(1)...├Än societ─â╚¢ile ├«n nume colectiv, ├«n comandit─â simpl─â ╚Öi ├«n cele cu r─âspundere limitat─â, asocia╚¢ii pot hot─âr├«, o dat─â cu dizolvarea, cu cvorumul ╚Öi majoritatea prev─âzute pentru modificarea actului constitutiv, ╚Öi modul de lichidare a societ─â╚¢ii, atunci c├ónd sunt de acord cu privire la repartizarea ╚Öi lichidarea patrimoniului societ─â╚¢ii ╚Öi c├ónd asigur─â stingerea pasivului sau regularizarea lui ├«n acord cu creditorii....(2)...Prin votul unanim al asocia╚¢ilor se poate hot─âr├« ╚Öi asupra modului ├«n care activele r─âmase dup─â plata creditorilor vor fi ├«mp─âr╚¢ite ├«ntre asocia╚¢i, caz ├«n care la cererea de radiere a societ─â╚¢ii se anexeaz─â ╚Öi dovada ├«ndeplinirii obliga╚¢iei de calculare, re╚¢inere ╚Öi plat─â a impozitului pe venit din lichidarea societ─â╚¢ii, prev─âzut─â la art. 97 alin. (5) din Legea nr. 227/2015 privind Codul fiscal, cu modific─ârile ╚Öi complet─ârile ulterioare, respectiv a impozitului pe veniturile realizate de nereziden╚¢i din lichidarea unui rezident, prev─âzut la art. 223 alin. (1) lit. o) coroborat cu art. 224 din aceea╚Öi lege. ├Än lipsa acordului unanim privind ├«mp─âr╚¢irea bunurilor, va fi urmat─â procedura lichid─ârii prev─âzut─â de prezenta lege.(la 23-07-2023, 
			Alineatul (2), Articolul 235, Capitolul I, Titlul VI a fost modificat de Punctul 4., Articolul I din LEGEA nr. 222 din 14 iulie 2023, publicat─â ├«n MONITORUL OFICIAL nr. 667 din 20 iulie 2023
			)...(3)...Transmiterea dreptului de proprietate asupra bunurilor r─âmase dup─â plata creditorilor are loc la data radierii societ─â╚¢ii din registrul comer╚¢ului.(la 01-12-2006, 
			Alin. (3) al art. 235 a fost introdus de pct. 157 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)...(4)...Registrul va elibera fiec─ârui asociat un certificat constatator al dreptului de proprietate asupra activelor distribuite, ├«n baza c─âruia asociatul poate proceda la ├«nscrierea bunurilor imobile ├«n cartea funciar─â.(la 01-12-2006, 
