INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (488, 4, '243', 'Articolul 243', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Fuziunea ╚Öi divizarea societ─â╚¢ilor', NULL, NULL, NULL, NULL, '(1)...Creditorii societ─â╚¢ilor care iau parte la fuziune sau la divizare au dreptul la o protec╚¢ie adecvat─â a intereselor lor. ├Än vederea ob╚¢inerii de garan╚¢ii adecvate, orice creditor care de╚¢ine o crean╚¢─â cert─â, lichid─â ╚Öi anterioar─â datei public─ârii proiectului de fuziune sau de divizare, ├«n una dintre modalit─â╚¢ile prev─âzute laart. 242, nescadent─â la data public─ârii, a c─ârei satisfacere este pus─â ├«n pericol prin realizarea fuziunii/diviz─ârii, poate face opozi╚¢ie, ├«n condi╚¢iile prezentului articol.(la 02-03-2012, 
