INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (504, 4, '250', 'Articolul 250', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Fuziunea ╚Öi divizarea societ─â╚¢ilor', NULL, NULL, NULL, NULL, '(1)...Fuziunea sau divizarea are urm─âtoarele consecin╚¢e:a)transferul, at├ót ├«n raporturile dintre societatea absorbit─â sau divizat─â ╚Öi societatea absorbant─â/societ─â╚¢ile beneficiare, c├ót ╚Öi ├«n raporturile cu ter╚¢ii, c─âtre societatea absorbant─â sau fiecare dintre societ─â╚¢ile beneficiare al tuturor activelor ╚Öi pasivelor societ─â╚¢ii absorbite/divizate; acest transfer va fi efectuat ├«n conformitate cu regulile de repartizare stabilite ├«n proiectul de fuziune/divizare;...b)ac╚¢ionarii sau asocia╚¢ii societ─â╚¢ii absorbite sau divizate devin ac╚¢ionari, respectiv asocia╚¢i ai societ─â╚¢ii absorbante, respectiv ai societ─â╚¢ilor beneficiare, ├«n conformitate cu regulile de repartizare stabilite ├«n proiectul de fuziune/divizare;...c)societatea absorbit─â sau divizat─â ├«nceteaz─â s─â existe.......(2)...Nicio ac╚¢iune sau parte social─â la societatea absorbant─â nu poate fi schimbat─â pentru ac╚¢iuni/p─âr╚¢i sociale emise de societatea absorbit─â ╚Öi care sunt de╚¢inute:a)de c─âtre societatea absorbant─â, direct sau prin intermediul unei persoane ac╚¢ion├ónd ├«n nume propriu, dar ├«n contul societ─â╚¢ii; sau...b)de c─âtre societatea absorbit─â, direct sau prin intermediul unei persoane ac╚¢ion├ónd ├«n nume propriu, dar ├«n contul societ─â╚¢ii.......(3)...Nicio ac╚¢iune sau parte social─â la una dintre societ─â╚¢ile beneficiare nu poate fi schimbat─â pentru ac╚¢iuni la societatea divizat─â, de╚¢inute:a)de c─âtre societatea beneficiar─â ├«n cauz─â, direct sau prin intermediul unei persoane ac╚¢ion├ónd ├«n nume propriu, dar pe seama societ─â╚¢ii; sau...b)de c─âtre societatea divizat─â, direct sau prin intermediul unei persoane ac╚¢ion├ónd ├«n nume propriu, dar pe seama societ─â╚¢ii.......(la 01-12-2006, 
			Art. 250 a fost modificat de pct. 171 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)', NULL, 325, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (505, 4, '250', 'Articolul 250', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Fuziunea ╚Öi divizarea societ─â╚¢ilor', NULL, NULL, NULL, NULL, 'Prevederile prezentului capitol referitoare la divizare, cu excep╚¢iaart. 250 alin. (1) lit. c), se aplic─â ╚Öi atunci c├ónd o parte din patrimoniul unei societ─â╚¢i se desprinde ╚Öi este transferat─â ca ├«ntreg uneia sau mai multor societ─â╚¢i existente ori unor societ─â╚¢i care sunt astfel constituite, ├«n schimbul aloc─ârii de ac╚¢iuni sau p─âr╚¢i sociale ale societ─â╚¢ilor beneficiare c─âtre:a)ac╚¢ionarii sau asocia╚¢ii societ─â╚¢ii care transfer─â activele (desprindere ├«n interesul ac╚¢ionarilor ori asocia╚¢ilor); sau...b)societatea care transfer─â activele (desprindere ├«n interesul societ─â╚¢ii).......(la 01-12-2006, 
			Art. 250^1 a fost introdus de pct. 172 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)', NULL, 326, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (506, 4, '251', 'Articolul 251', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Fuziunea ╚Öi divizarea societ─â╚¢ilor', NULL, NULL, NULL, NULL, '(1)...Nulitatea unei fuziuni sau diviz─âri poate fi declarat─â numai prin hot─âr├óre judec─âtoreasc─â....(2)...De la data realiz─ârii sale, potrivitart. 249, fuziunea, respectiv divizarea poate fi declarat─â nul─â doar dac─â nu a fost supus─â unui control de legalitate sau dac─â hot─âr├órea uneia dintre adun─ârile generale care au votat proiectul fuziunii sau al diviz─ârii este nul─â ori anulabil─â.(la 26-11-2022, 
			Alineatul (2) din Articolul 251 , Capitolul II , Titlul VI  a fost modificat de Punctul 56, Articolul 129, Sectiunea a 10-a, Capitolul IV din LEGEA nr. 265 din 22 iulie 2022, publicat─â ├«n MONITORUL OFICIAL nr. 750 din 26 iulie 2022
			)...(3)...Procedurile de anulare ╚Öi de declarare a nulit─â╚¢ii fuziunii sau diviz─ârii nu pot fi ini╚¢iate dup─â expirarea unui termen de 6 luni de la data la care fuziunea sau divizarea a devenit efectiv─â, ├«n temeiulart. 249, sau dac─â situa╚¢ia a fost rectificat─â.(la 30-04-2008, 
			Alin. (3) al art. 251 a fost modificat de pct. 9 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 52 din 21 aprilie 2008, publicat─â ├«n MONITORUL OFICIAL nr. 333 din 30 aprilie 2008.
			)...(4)...Dac─â neregularitatea ce poate conduce la declararea nulit─â╚¢ii unei fuziuni sau diviz─âri poate fi remediat─â, instan╚¢a competent─â acord─â societ─â╚¢ilor implicate un termen pentru rectificarea acesteia....(5)...Hot─âr├órea definitiv─â de declarare a nulit─â╚¢ii unei fuziuni sau diviz─âri va fi ├«naintat─â din oficiu de c─âtre instan╚¢─â oficiilor registrului comer╚¢ului de la sediile societ─â╚¢ilor implicate ├«n fuziunea sau divizarea respectiv─â....(6)...Hot─âr├órea definitiv─â de declarare a nulit─â╚¢ii unei fuziuni ori diviz─âri nu aduce atingere prin ea ├«ns─â╚Öi valabilit─â╚¢ii obliga╚¢iilor n─âscute ├«n sarcina sau ├«n beneficiul societ─â╚¢ii absorbante ori societ─â╚¢ilor beneficiare, angajate dup─â ce fuziunea sau divizarea au devenit efective, ├«n temeiulart. 249, ╚Öi ├«nainte ca hot─âr├órea de declarare a nulit─â╚¢ii s─â fie publicat─â....(7)...├Än cazul declar─ârii nulit─â╚¢ii unei fuziuni, societ─â╚¢ile participante la fuziunea respectiv─â r─âspund solidar pentru obliga╚¢iile societ─â╚¢ii absorbante, angajate ├«n perioada men╚¢ionat─â laalin. (6)....(8)...├Än cazul declar─ârii nulit─â╚¢ii unei diviz─âri, fiecare dintre societ─â╚¢ile beneficiare r─âspunde pentru propriile obliga╚¢ii, angajate ├«n perioada prev─âzut─â laalin. (6). Societatea divizat─â r─âspunde, de asemenea, pentru aceste obliga╚¢ii, ├«n limita cotei de active nete transferate societ─â╚¢ii beneficiare ├«n contul c─âreia au luat na╚Ötere obliga╚¢iile respective....(la 01-12-2006, 
			Art. 251 a fost modificat de pct. 173 al art. I din LEGE nr. 441 din 27 noiembrie 2006, publicat─â ├«n MONITORUL OFICIAL nr. 955 din 28 noiembrie 2006.
			)', NULL, 327, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
