INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (526, 4, '251', 'Articolul 251', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Fuziunea transfrontalier─â', NULL, 'Domeniul de aplicare. Defini╚¢ie', NULL, NULL, '(1)...Societ─â╚¢ile pe ac╚¢iuni, societ─â╚¢ile ├«n comandit─â pe ac╚¢iuni, societ─â╚¢ile cu r─âspundere limitat─â - persoane juridice rom├óne - ╚Öi societ─â╚¢ile europene cu sediul social ├«n Rom├ónia pot fuziona, potrivit prezentei legi, cu societ─â╚¢i care au sediul social sau, dup─â caz, administra╚¢ia central─â ori sediul principal ├«n alte state membre ale Uniunii Europene sau state participante la Asocia╚¢ia European─â a Liberului Schimb, denumite ├«n continuare state membre, care sunt guvernate de legisla╚¢ia acestora ╚Öi func╚¢ioneaz─â ├«n una dintre formele juridice prev─âzute ├«n anexa II la Directiva (UE) 2017/1.132 a Parlamentului European ╚Öi a Consiliului din 14 iunie 2017 privind anumite aspecte ale dreptului societ─â╚¢ilor comerciale, publicat─â ├«n Jurnalul Oficial al Uniunii Europene, seria L, nr. 169 din 30 iunie 2017....(2)...Societ─â╚¢ile pe ac╚¢iuni, societ─â╚¢ile ├«n comandit─â pe ac╚¢iuni, societ─â╚¢ile cu r─âspundere limitat─â - persoane juridice rom├óne - ╚Öi societ─â╚¢ile europene cu sediul social ├«n Rom├ónia pot fuziona cu societ─â╚¢i care au sediul social sau, dup─â caz, administra╚¢ia central─â ori sediul principal ├«n alte state membre ╚Öi care, f─âr─â a se ├«ncadra ├«n tipurile de entit─â╚¢i prev─âzute laalin. (1), au personalitate juridic─â, de╚¢in un patrimoniu propriu ce reprezint─â singura surs─â care asigur─â garantarea obliga╚¢iilor sociale ╚Öi sunt supuse unor formalit─â╚¢i de publicitate similare celor prev─âzute de Directiva (UE) 2017/1.132, dac─â legea acelui stat membru permite astfel de fuziuni....(3)...├Än cazul ├«n care societatea absorbant─â este o societate ├«n comandit─â pe ac╚¢iuni, ├«nfiin╚¢at─â ╚Öi func╚¢ion├ónd potrivit legii rom├óne, ac╚¢ionarii societ─â╚¢ii absorbite vor fi ├«ntotdeauna ac╚¢ionari comanditari ai societ─â╚¢ii ├«n comandit─â pe ac╚¢iuni absorbante, dac─â nu se prevede altfel ├«n hot─âr├órea de aprobare a proiectului de fuziune....(la 23-07-2023, 
			Sectiunea 1, Capitolul IV, Titlul VI a fost completat─â de Punctul 6., Articolul I din LEGEA nr. 222 din 14 iulie 2023, publicat─â ├«n MONITORUL OFICIAL nr. 667 din 20 iulie 2023
