INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (527, 4, '251', 'Articolul 251', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Fuziunea transfrontalier─â', NULL, 'Domeniul de aplicare. Defini╚¢ie', NULL, NULL, 'Sunt exceptate de la aplicarea prevederilor prezentului capitol:a)societ─â╚¢ile reglementate de Legea nr. 297/2004, cu modific─ârile ╚Öi complet─ârile ulterioare, de Ordonan╚¢a de urgen╚¢─â a Guvernului nr. 32/2012 privind organismele de plasament colectiv ├«n valori mobiliare ╚Öi societ─â╚¢ile de administrare a investi╚¢iilor, precum ╚Öi pentru modificarea ╚Öi completarea Legii nr. 297/2004 privind pia╚¢a de capital, aprobat─â cu modific─âri ╚Öi complet─âri prin Legea nr. 10/2015, cu modific─ârile ╚Öi complet─ârile ulterioare, de Legea nr. 74/2015 privind administratorii de fonduri de investi╚¢ii alternative, cu modific─ârile ╚Öi complet─ârile ulterioare, de Legea nr. 24/2017 privind emiten╚¢ii de instrumente financiare ╚Öi opera╚¢iuni de pia╚¢─â, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare, de Legea nr. 126/2018 privind pie╚¢ele de instrumente financiare, cu modific─ârile ╚Öi complet─ârile ulterioare, de Legea nr. 243/2019 privind reglementarea fondurilor de investi╚¢ii alternative ╚Öi pentru modificarea ╚Öi completarea unor acte normative;...b)societ─â╚¢ile care fac obiectul instrumentelor, competen╚¢elor ╚Öi mecanismelor de rezolu╚¢ie, al m─âsurilor de redresare ╚Öi rezolu╚¢ie ╚Öi al m─âsurilor de prevenire a crizelor prev─âzute de Legea nr. 312/2015 privind redresarea ╚Öi rezolu╚¢ia institu╚¢iilor de credit ╚Öi a firmelor de investi╚¢ii, precum ╚Öi pentru modificarea ╚Öi completarea unor acte normative ├«n domeniul financiar, cu modific─ârile ╚Öi complet─ârile ulterioare;...c)societ─â╚¢ile care se afl─â ├«n curs de lichidare ╚Öi care au ├«nceput s─â distribuie activele c─âtre asocia╚¢i;...d)societ─â╚¢ile aflate ├«ntr-o procedur─â de insolven╚¢─â sau de prevenire a insolven╚¢ei, prev─âzut─â de Legea nr. 85/2014 privind procedurile de prevenire a insolven╚¢ei ╚Öi de insolven╚¢─â, cu modific─ârile ╚Öi complet─ârile ulterioare;...e)opera╚¢iunile de fuziune supuse Regulamentului (CE) nr. 2.157/2001 al Consiliului din 8 octombrie 2001 privind statutul societ─â╚¢ii europene (SE), publicat ├«n Jurnalul Oficial al Uniunii Europene, seria L, nr. 294 din 10 noiembrie 2001.......(la 23-07-2023, 
