INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (529, 4, '251', 'Articolul 251', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Fuziunea transfrontalier─â', NULL, 'Domeniul de aplicare. Defini╚¢ie', NULL, NULL, '(1)...├Än sensul prezentei legi, fuziunea transfrontalier─â este opera╚¢iunea prin care:a)una sau mai multe societ─â╚¢i, dintre cele enumerate ├«n anexa II a Directivei (UE) 2017/1.132, dintre care cel pu╚¢in dou─â sunt guvernate de legisla╚¢ia a dou─â state membre diferite, sunt dizolvate f─âr─â a intra ├«n lichidare ╚Öi transfer─â totalitatea activelor ╚Öi pasivelor lor unei alte societ─â╚¢i ├«n schimbul repartiz─ârii c─âtre asocia╚¢ii societ─â╚¢ii sau societ─â╚¢ilor absorbite de ac╚¢iuni, p─âr╚¢i sociale sau titluri de valoare reprezent├ónd capitalul social la societatea absorbant─â ╚Öi, eventual, al unei pl─â╚¢i ├«n numerar de maximum 10% din valoarea nominal─â a ac╚¢iunilor, p─âr╚¢ilor sociale sau altor titluri de valoare reprezent├ónd capitalul social astfel repartizate; sau...b)mai multe societ─â╚¢i, dintre cele enumerate ├«n anexa II a Directivei (UE) 2017/1.132, dintre care cel pu╚¢in dou─â sunt guvernate de legisla╚¢ia a dou─â state membre diferite, sunt dizolvate f─âr─â a intra ├«n lichidare ╚Öi transfer─â totalitatea activelor ╚Öi pasivelor lor unei societ─â╚¢i pe care o constituie, ├«n schimbul repartiz─ârii c─âtre asocia╚¢ii lor de ac╚¢iuni, p─âr╚¢i sociale sau alte titluri de valoare reprezent├ónd capitalul social la societatea nou-constituit─â ╚Öi, eventual, al unei pl─â╚¢i ├«n numerar de maximum 10% din valoarea nominal─â a ac╚¢iunilor, p─âr╚¢ilor sociale sau altor titluri de valoare reprezent├ónd capitalul social astfel repartizate;...c)o societate, dintre cele enumerate ├«n anexa II a Directivei (UE) 2017/1.132, este dizolvat─â f─âr─â a intra ├«n lichidare ╚Öi transfer─â totalitatea activelor ╚Öi pasivelor sale unei alte societ─â╚¢i guvernate de legisla╚¢ia altui stat membru, care de╚¢ine totalitatea ac╚¢iunilor sale, p─âr╚¢ilor sale sociale sau a altor titluri de valoare reprezent├ónd capitalul social;...d)una sau mai multe societ─â╚¢i, dintre cele enumerate ├«n anexa II a Directivei (UE) 2017/1.132, ├«╚Öi transfer─â, ca urmare a dizolv─ârii f─âr─â lichidare, toate activele ╚Öi pasivele unei alte societ─â╚¢i existente, societatea absorbant─â, f─âr─â emiterea de noi ac╚¢iuni, p─âr╚¢i sociale sau alte titluri de valoare reprezent├ónd capitalul social de c─âtre societatea absorbant─â, cu condi╚¢ia ca o persoan─â s─â de╚¢in─â direct sau indirect totalitatea ac╚¢iunilor, p─âr╚¢ilor sociale sau altor titluri de valoare reprezent├ónd capitalul social al societ─â╚¢ilor care fuzioneaz─â sau cu condi╚¢ia ca asocia╚¢ii societ─â╚¢ilor care fuzioneaz─â s─â de╚¢in─â ac╚¢iunile, p─âr╚¢ile sociale sau alte titluri de valoare reprezent├ónd capitalul social ├«n aceea╚Öi propor╚¢ie ├«n toate societ─â╚¢ile care fuzioneaz─â.......(2)...Plata ├«n numerar poate fi superioar─â valorii prev─âzute laalin. (1) lit. a)╚Öib), dac─â cel pu╚¢in legisla╚¢ia unuia dintre statele membre a c─âror na╚¢ionalitate o de╚¢in societ─â╚¢ile participante la fuziune sau societatea nou-constituit─â permite dep─â╚Öirea acestui procent....(la 23-07-2023, 
