INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (568, 4, '251', 'Articolul 251', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Divizarea transfrontalier─â', NULL, 'Domeniul de aplicare. Defini╚¢ie', NULL, NULL, '(1)...Legea rom├ón─â este legea aplicabil─â procedurilor ╚Öi formalit─â╚¢ilor diviz─ârii transfrontaliere ├«n vederea ob╚¢inerii certificatului prealabil de c─âtre societatea persoan─â juridic─â rom├ón─â care face obiectul diviz─ârii, respectiv procedurilor ╚Öi formalit─â╚¢ilor derulate ├«n vederea ├«nmatricul─ârii ├«n registrul comer╚¢ului a societ─â╚¢ii/societ─â╚¢ilor beneficiare a/ale unei diviz─âri transfrontaliere, persoane juridice rom├óne. Legea rom├ón─â stabile╚Öte data de la care divizarea transfrontalier─â produce efecte dac─â societatea care face obiectul diviz─ârii este persoan─â juridic─â rom├ón─â....(2)...Societatea beneficiar─â/Societ─â╚¢ile beneficiare constituit─â/ constituite ├«n una dintre formele de societate prev─âzute laart. 251^59trebuie s─â ├«ntruneasc─â cerin╚¢ele prev─âzute de legea rom├ón─â pentru constituirea acelei forme de societate....(la 23-07-2023, 
