INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (580, 4, '251', 'Articolul 251', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Divizarea transfrontalier─â', NULL, 'Etape. Efecte. Nulitate', NULL, NULL, '(1)...Oficiul Na╚¢ional al Registrului Comer╚¢ului transmite certificatul prealabil diviz─ârii transfrontaliere autorit─â╚¢ilor competente pentru verificarea legalit─â╚¢ii procedurilor de derulare a diviz─ârii transfrontaliere din statele membre a c─âror legisla╚¢ie guverneaz─â societ─â╚¢ile beneficiare ╚Öi registrelor comer╚¢ului din aceste state membre, prin intermediul sistemelor de interconectare a registrelor comer╚¢ului, ╚Öi asigur─â accesul ter╚¢ilor la acest certificat, inclusiv prin intermediul sistemului de interconectare a registrului comer╚¢ului....(2)...├Än cazul unei diviz─âri complete ├«n care societ─â╚¢ile beneficiare sunt guvernate de legisla╚¢ia altui stat membru/altor state membre, oficiul registrul comer╚¢ului ├«nscrie, din oficiu sau, dup─â caz, la cererea societ─â╚¢ii ce face obiectul diviz─ârii sau a societ─â╚¢ilor beneficiare, men╚¢iunea c─â radierea din registrul comer╚¢ului este rezultatul unei diviz─âri transfrontaliere, precum ╚Öi numerele de ├«nregistrare, firma/denumirea ╚Öi forma juridic─â ale societ─â╚¢ilor beneficiare din celelalte state membre....(3)...├Än cazul unei diviz─âri complete ├«n care cel pu╚¢in una dintre societ─â╚¢ile beneficiare este guvernat─â de legea rom├ón─â, oficiul registrului comer╚¢ului ├«nscrie, din oficiu sau, dup─â caz, la cererea societ─â╚¢ii ce face obiectul diviz─ârii sau a societ─â╚¢ilor beneficiare, men╚¢iunea c─â radierea din registrul comer╚¢ului este rezultatul unei diviz─âri transfrontaliere, precum ╚Öi num─ârul de ├«nregistrare, firma ╚Öi forma juridic─â ale societ─â╚¢ii/societ─â╚¢ilor beneficiare ├«nregistrate ├«n registrul comer╚¢ului din Rom├ónia ╚Öi numerele de ├«nregistrare, firma/denumirea ╚Öi forma juridic─â ale societ─â╚¢ilor beneficiare din celelalte state membre....(4)...├Än cazul prev─âzut laalin. (3), la cererea societ─â╚¢ii ce face obiectul diviz─ârii, formulat─â odat─â cu cererea de emitere a certificatului prealabil diviz─ârii transfrontaliere, registratorul dispune ╚Öi cu privire la ├«nregistrarea ├«n registrul comer╚¢ului a societ─â╚¢ii/societ─â╚¢ilor beneficiare guvernate de legea rom├ón─â....(5)...├Än cazul unei diviz─âri transfrontaliere complete, ├«n termen de o zi lucr─âtoare de la data primirii, prin intermediul sistemului de interconectare a registrelor comer╚¢ului, a tuturor notific─ârilor c─â societ─â╚¢ile beneficiare au fost ├«nregistrate, oficiul registrului comer╚¢ului radiaz─â societatea ce a f─âcut obiectul diviz─ârii din registrul comer╚¢ului, ├«nregistr├ónd ╚Öi men╚¢iunea c─â radierea este rezultatul unei diviz─âri transfrontaliere, ╚Öi notific─â, prin sistemul de interconectare a registrelor comer╚¢ului, c─â divizarea produce efecte....(6)...├Än cazul unei diviz─âri transfrontaliere par╚¢iale sau prin separare, ├«n termen de o zi lucr─âtoare de la data primirii, prin intermediul sistemului de interconectare a registrelor comer╚¢ului, a tuturor notific─ârilor c─â societ─â╚¢ile beneficiare au fost ├«nregistrate, oficiul registrului comer╚¢ului ├«nregistreaz─â modificarea actului constitutiv al societ─â╚¢ii ce face obiectul diviz─ârii ╚Öi men╚¢iunea c─â aceasta este rezultatul unei diviz─âri transfrontaliere. Oficiul registrului comer╚¢ului notific─â, prin sistemul de interconectare a registrelor comer╚¢ului, c─â divizarea produce efecte....(la 23-07-2023, 
			Sectiunea a 2-a, Capitolul VI, Titlul VI a fost completat─â de Punctul 6., Articolul I din LEGEA nr. 222 din 14 iulie 2023, publicat─â ├«n MONITORUL OFICIAL nr. 667 din 20 iulie 2023
			)', NULL, 401, '2025-11-14 21:45:02.035729', '2025-11-14 21:45:02.035729', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (581, 4, '251', 'Articolul 251', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Divizarea transfrontalier─â', NULL, 'Etape. Efecte. Nulitate', NULL, NULL, '(1)...Competen╚¢a de verificare a ├«ndeplinirii cerin╚¢elor legale pentru ├«nmatricularea ├«n registrul comer╚¢ului a unei societ─â╚¢i pe ac╚¢iuni, ├«n comandit─â pe ac╚¢iuni sau a unei societ─â╚¢i cu r─âspundere limitat─â, persoan─â juridic─â rom├ón─â, societate beneficiar─â rezultat─â din divizarea transfrontalier─â a unei societ─â╚¢i ├«nregistrate ├«n registrul comer╚¢ului dintr-un alt stat membru, precum ╚Öi a ├«ndeplinirii, dac─â este cazul, a cerin╚¢elor legale referitoare la participarea angaja╚¢ilor ╚Öi la alte modalit─â╚¢i de implicare a acestora apar╚¢ine registratorului de la oficiul registrului comer╚¢ului ├«n a c─ârui circumscrip╚¢ie se afl─â sediul social al societ─â╚¢ii beneficiare. ├Än cazul ├«n care mai multe societ─â╚¢i beneficiare sunt guvernate de legea rom├ón─â, competen╚¢a de verificare apar╚¢ine registratorului de la oficiul registrului comer╚¢ului ├«n a c─ârui circumscrip╚¢ie se afl─â sediul social al oric─âreia dintre societ─â╚¢ile beneficiare....(2)...La cererea societ─â╚¢ii beneficiare, depus─â potrivit prevederilor art. 84 din Legea nr. 265/2022, registratorul, constat├ónd ├«ndeplinite prevederile legale de constituire ╚Öi  ├«nmatriculare a societ─â╚¢ii ╚Öi fiind recep╚¢ionat certificatul prealabil diviz─ârii transfrontaliere emis de autoritatea competent─â din statul membru de plecare, dispune ├«nmatricularea societ─â╚¢ii beneficiare ╚Öi a men╚¢iunii c─â societatea ├«nmatriculat─â este rezultatul unei diviz─âri transfrontaliere, precum ╚Öi num─ârul de ├«nregistrare, firma/denumirea ╚Öi forma juridic─â ale societ─â╚¢ii divizate....(3)...Certificatul prealabil diviz─ârii transfrontaliere emis de autoritatea competent─â din statul membru de plecare atest─â ├«ndeplinirea condi╚¢iilor privind procedurile de derulare ╚Öi formalit─â╚¢ile transform─ârii transfrontaliere ├«n statul membru de plecare....(4)...Oficiul Na╚¢ional al Registrului Comer╚¢ului notific─â, de ├«ndat─â, registrului comer╚¢ului din statul membru a c─ârui legisla╚¢ie a guvernat societatea ce a f─âcut obiectul diviz─ârii, prin sistemul de interconectare a registrului comer╚¢ului, faptul c─â societatea beneficiar─â a fost ├«nmatriculat─â ├«n registrul comer╚¢ului....(la 23-07-2023, 
