INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (582, 4, '251', 'Articolul 251', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Divizarea transfrontalier─â', NULL, 'Etape. Efecte. Nulitate', NULL, NULL, '(1)...Divizarea transfrontalier─â a unei societ─â╚¢i persoan─â juridic─â rom├ón─â produce efecte la data radierii din registrul comer╚¢ului a societ─â╚¢ii care a f─âcut obiectul diviz─ârii sau, dup─â caz, la data ├«nregistr─ârii ├«n registrul comer╚¢ului a modific─ârii actului constitutiv al acesteia....(2)...├Än cazul diviz─ârii transfrontaliere a unei societ─â╚¢i guvernate de legea altui stat membru care are ca rezultat constituirea a cel pu╚¢in unei societ─â╚¢i beneficiare persoan─â juridic─â rom├ón─â, divizarea va produce efecte potrivit legii aplicabile societ─â╚¢ii ce a f─âcut obiectul diviz─ârii....(la 23-07-2023, 
