INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (586, 4, '251', 'Articolul 251', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Divizarea transfrontalier─â', NULL, 'Etape. Efecte. Nulitate', NULL, NULL, '(1)...Divizarea transfrontalier─â care a fost supus─â controlului de legalitate, potrivit prezentei legi, nu poate fi desfiin╚¢at─â dup─â data la care aceasta produce efecte....(2)...Prin excep╚¢ie de la prevederileart. 132 alin. (3), ac╚¢iunea ├«n declararea nulit─â╚¢ii hot─âr├órii adun─ârii generale a asocia╚¢ilor de aprobare a diviz─ârii transfrontaliere poate fi introdus─â ├«n termen de 30 de zile de la data public─ârii acesteia ├«n Monitorul Oficial al Rom├óniei....(3)...├Än cadrul unei ac╚¢iuni ├«n anulare, formulate potrivit prevederilorart. 132 alin. (2), hot─âr├órea adun─ârii generale a asocia╚¢ilor nu poate fi anulat─â exclusiv pentru:a)modul de stabilire a ratei de schimb a ac╚¢iunilor, p─âr╚¢ilor sociale sau altor titluri de valoare reprezent├ónd capitalul social;...b)modul necorespunz─âtor de stabilire a pre╚¢ului ac╚¢iunilor, p─âr╚¢ilor sociale sau altor titluri de valoare reprezent├ónd capitalul social pentru situa╚¢ia exercit─ârii de c─âtre asocia╚¢i a dreptului retragerii din societate;...c)nerespectarea cerin╚¢elor privind informarea asocia╚¢ilor asupra pre╚¢ului ac╚¢iunilor, p─âr╚¢ilor sociale sau altor titluri de  valoare reprezent├ónd capitalul social pentru situa╚¢ia exercit─ârii dreptului retragerii din societate sau asupra ratei de schimb a ac╚¢iunilor, p─âr╚¢ilor sociale sau altor titluri de valoare reprezent├ónd capitalul social.......(4)...Procedurile de anulare ╚Öi de declarare a nulit─â╚¢ii nu pot fi ini╚¢iate dac─â situa╚¢ia a fost rectificat─â. Dac─â neregularitatea ce poate conduce la declararea nulit─â╚¢ii unei diviz─âri transfrontaliere poate fi remediat─â, instan╚¢a competent─â acord─â un termen pentru rectificarea acesteia....(5)...R─âspunderea penal─â ╚Öi consecin╚¢ele acesteia, ├«n cazul s─âv├ór╚Öirii unei infrac╚¢iuni de c─âtre societatea ce a f─âcut obiectul diviz─ârii transfrontaliere, se vor angaja:a)├«n cazul unei diviz─âri complete, ├«n sarcina societ─â╚¢ii/ societ─â╚¢ilor beneficiare, potrivit legii;...b)├«n cazul unei diviz─âri par╚¢iale sau al unei diviz─âri prin separare, at├ót ├«n sarcina societ─â╚¢ii care a f─âcut obiectul diviz─ârii, c├ót ╚Öi ├«n sarcina societ─â╚¢ii/societ─â╚¢ilor beneficiare.......(la 23-07-2023, 
