INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (591, 4, '255', 'Articolul 255', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, '(1)...├Än afar─â de puterile conferite de asocia╚¢i, cu aceea╚Öi majoritate cerut─â pentru numirea lor, lichidatorii vor putea:a)s─â stea ├«n judecat─â ├«n numele societ─â╚¢ii;(la 29-06-2007, 
			Lit. a) a alin. (1) al art. 255 a fost modificat─â de pct. 63 al art. I din ORDONAN╚ÜA DE URGEN╚Ü─é nr. 82 din 28 iunie 2007, publicat─â ├«n MONITORUL OFICIAL nr. 446 din 29 iunie 2007.
			)...b)s─â execute ╚Öi s─â termine opera╚¢iunile de comer╚¢ referitoare la lichidare;...c)s─â v├ónd─â, prin licita╚¢ie public─â, imobilele ╚Öi orice avere mobiliar─â a societ─â╚¢ii;(la 29-06-2007, 
