INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (602, 4, '265', 'Articolul 265', NULL, 'Excluderea ╚Öi retragerea asocia╚¢ilor', NULL, 'Lichidarea societ─â╚¢ilor pe ac╚¢iuni ╚Öi ├«n comandit─â pe ac╚¢iuni', NULL, NULL, NULL, NULL, '(1)...Administratorii, respectiv membrii directoratului, vor prezenta lichidatorilor o dare de seam─â asupra gestiunii, pentru timpul trecut de la ultima situa╚¢ie financiar─â aprobat─â p├ón─â la ├«nceperea lichid─ârii.(la 01-12-2006, 
