INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (613, 4, '270', 'Articolul 270', NULL, 'Societatea european─â', NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Creditorii societ─â╚¢ilor europene ale c─âror crean╚¢e sunt anterioare datei public─ârii proiectului de transfer ╚Öi care nu sunt scadente la data public─ârii pot face opozi╚¢ie ├«n condi╚¢iileart. 62....(2)...Opozi╚¢ia prev─âzut─â laalin. (1)suspend─â executarea opera╚¢iunii p├ón─â la data la care hot─âr├órea judec─âtoreasc─â r─âm├óne definitiv─â, ├«n afar─â de cazul ├«n care societatea debitoare face dovada pl─â╚¢ii datoriilor sau ofer─â garan╚¢ii acceptate de creditori ori ├«ncheie cu ace╚Ötia un acord pentru plata datoriilor.(la 02-06-2012, 
