INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (614, 4, '270', 'Articolul 270', NULL, 'Societatea european─â', NULL, NULL, NULL, NULL, NULL, NULL, '(1)...Ac╚¢ionarii care nu au votat ├«n favoarea hot─âr├órii adun─ârii generale prin care a fost aprobat transferul sediului ├«ntr-un alt stat membru au dreptul de a se retrage din societate ╚Öi de a solicita cump─ârarea ac╚¢iunilor lor de c─âtre societate....(2)...Dreptul de retragere poate fi exercitat ├«n termen de 30 de zile de la data adopt─ârii hot─âr├órii adun─ârii generale....(3)...Ac╚¢ionarii vor depune la sediul societ─â╚¢ii, al─âturi de declara╚¢ia scris─â de retragere, ac╚¢iunile pe care le posed─â sau, dup─â caz, certificatele de ac╚¢ionar....(4)...Pre╚¢ul pl─âtit de societate pentru ac╚¢iunile celui ce exercit─â dreptul de retragere va fi stabilit de un expert autorizat independent, ca valoare medie ce rezult─â din aplicarea a cel pu╚¢in dou─â metode de evaluare recunoscute de legisla╚¢ia ├«n vigoare la data evalu─ârii. Expertul este numit de registratorul de registrul comer╚¢ului, ├«n conformitate cu dispozi╚¢iileart. 38╚Öi39. Costurile de evaluare vor fi suportate de societate.(la 26-11-2022, 
