INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1087, 1, '40', 'Articolul 40', NULL, NULL, NULL, 'Taxe ╚Öi redeven╚¢e', NULL, NULL, NULL, NULL, 'Plata taxelor, a tarifelor ╚Öi a redeven╚¢elor prev─âzute ├«n prezenta lege pentru concesionarul perimetrului eolian offshore constituie o obliga╚¢ie distinct─â de restul obliga╚¢iilor fiscale.', NULL, 40, '2025-11-14 21:57:25.34042', '2025-11-14 21:57:25.34042', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1323, 2, '1', 'Articolul 1', NULL, NULL, NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, '(1)...Prezenta lege reglementeaz─â cadrul general necesar implement─ârii ├«n Rom├ónia a proiectelor de producere a energiei electrice din resurse eoliene offshore....(2)...Implementarea proiectelor de producere a energiei electrice din resurse eoliene offshore se realizeaz─â cu respectarea principiului nediscrimin─ârii, ├«n condi╚¢ii de concuren╚¢─â loial─â, ╚Öi cu respectarea principiului de prevenire ╚Öi controlul integrat al polu─ârii prin utilizarea celor mai bune tehnici disponibile pentru activit─â╚¢ile cu impact semnificativ asupra mediului.', NULL, 1, '2025-11-14 21:57:26.235791', '2025-11-14 21:57:26.235791', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1324, 2, '2', 'Articolul 2', NULL, NULL, NULL, 'Dispozi╚¢ii generale', NULL, NULL, NULL, NULL, '├Än sensul prezentei legi, termenii ╚Öi expresiile de mai jos au urm─âtoarele semnifica╚¢ii:a)aprobare de dezvoltare a centralei electrice eoliene offshore - act asimilat, din punctul de vedere al efectelor juridice, autoriza╚¢iei de construire, reprezent├ónd autoriza╚¢ia de construire/desfiin╚¢are pentru lucr─ârile efectuate ├«ntr-un perimetru offshore determinat, precum ╚Öi pentru cele efectuate pe uscat, p├ón─â la punctul de racordare cu re╚¢eaua de transport al energiei electrice, acordat─â de c─âtre Autoritatea Competent─â de Reglementare a Opera╚¢iunilor Petroliere Offshore la Marea Neagr─â concesionarului unui perimetru eolian offshore;...b)autoritate competent─â pentru concesionarea perimetrelor offshore - Ministerul Energiei;...c)autoritate competent─â pentru emiterea aprob─ârii de dezvoltare/desfiin╚¢are a centralelor electrice eoliene offshore -Autoritatea Competent─â de Reglementare a Opera╚¢iunilor Petroliere Offshore la Marea Neagr─â, denumit─â ├«n continuare ACROPO;...d)autoritate competent─â pentru emiterea autoriza╚¢iei de ├«nfiin╚¢are ╚Öi a licen╚¢ei de producere a energiei electrice pentru activit─â╚¢ile supuse regimului autoriz─ârii - Autoritatea Na╚¢ional─â de Reglementare ├«n Domeniul Energiei, denumit─â ├«n continuare ANRE, conform prevederilor art. 8 din Legea energiei electrice ╚Öi a gazelor naturale nr. 123/2012, cu modific─ârile ╚Öi complet─ârile ulterioare;...e)autoriza╚¢ie de ├«nfiin╚¢are - autoriza╚¢ie emis─â de c─âtre ANRE pentru realizarea unor capacit─â╚¢i energetice de producere a energiei electrice, ├«n conformitate cu prevederile art. 8-10 din Legea nr. 123/2012,  cu modific─ârile ╚Öi complet─ârile ulterioare;...f)cablu subacvatic - cablu instalat pe fundul m─ârii, utilizat pentru transportul energiei electrice produse ├«n centralele electrice eoliene offshore;...g)central─â electric─â eolian─â offshore - capacitate de producere a energiei electrice situat─â ├«ntr-o zon─â a perimetrului eolian offshore concesionat, care grupeaz─â mai multe turbine eoliene ╚Öi instala╚¢ii electrice aferente, cu scopul de a transforma energia eolian─â primar─â ├«n energie electric─â;...h)concesionar - operatorul economic care a ├«ncheiat cu Ministerul Energiei un contract de concesiune pentru explorarea perimetrului eolian offshore ╚Öi exploatarea ulterioar─â a acestui perimetru prin construirea ╚Öi exploatarea unei centrale electrice eoliene offshore;...i)contract de concesiune - contract ├«ncheiat ├«ntre Ministerul Energiei ╚Öi concesionar ├«n vederea desf─â╚Öur─ârii activit─â╚¢ilor specifice de explorare a perimetrului eolian offshore, respectiv de exploatare a acestui perimetru prin construirea ╚Öi exploatarea centralelor electrice eoliene offshore;...j)desfiin╚¢are - retragerea din exploatare a centralei electrice eoliene offshore, ├«n baza autoriza╚¢iei ACROPO, concretizat─â prin lucr─âri privind demontarea construc╚¢iilor ce o alc─âtuiesc, gestionarea de╚Öeurilor rezultate ╚Öi repunerea perimetrului ├«n situa╚¢ia anterioar─â exploat─ârii ╚Öi construirii centralei electrice eoliene offshore;...k)dezvoltatorul centralei electrice eoliene offshore - operatorul economic care a ├«ncheiat contract de concesiune pentru explorarea unui perimetru eolian offshore ╚Öi pentru exploatarea acestui perimetru prin construirea ╚Öi exploatarea centralei electrice eoliene offshore, acesta reprezent├ónd gestionarul instala╚¢iei de producere a energiei electrice, ├«n conformitate cu prevederile art. 2 pct. 7 din Regulamentul (UE) 2016/631 al Comisiei din 14 aprilie 2016 de instituire a unui cod de re╚¢ea privind cerin╚¢ele pentru racordarea la re╚¢ea a instala╚¢iilor de generare;...l)licen╚¢─â de producere a energiei electrice - licen╚¢─â acordat─â de c─âtre ANRE unui operator economic pentru exploatarea comercial─â a capacit─â╚¢ilor de producere a energiei electrice ╚Öi, dup─â caz, a instala╚¢iilor de stocare a energiei ad─âugate capacit─â╚¢ilor de producere respective;...m)offshore - situat ├«n apele maritime interioare, marea teritorial─â, ├«n zona contigu─â ╚Öi ├«n zona economic─â exclusiv─â sau pe platoul continental al M─ârii Negre, ├«n sensul dispozi╚¢iilor Conven╚¢iei Na╚¢iunilor Unite asupra dreptului m─ârii, ├«ncheiat─â la Montego Bay (Jamaica) la 10 decembrie 1982, ratificat─â prin Legea nr. 110/1996, potrivit dispozi╚¢iilor art. 2 pct. 24 din Legea nr. 165/2016 privind siguran╚¢a opera╚¢iunilor petroliere offshore, cu modific─ârile ╚Öi complet─ârile ulterioare;...n)operator economic - persoan─â juridic─â de na╚¢ionalitate rom├ón─â sau str─âin─â care particip─â la procedurile organizate de autoritatea competent─â pentru ob╚¢inerea concesiunii, autoriza╚¢iilor, licen╚¢elor ├«n vederea explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore ╚Öi care este ├«nfiin╚¢at─â ╚Öi func╚¢ioneaz─â ├«n condi╚¢iile Legii societ─â╚¢ilor nr. 31/1990, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare, ori asocierea unor astfel de persoane juridice; documentul de asociere trebuie s─â cuprind─â, ├«n mod obligatoriu, urm─âtoarele elemente: capacitatea tehnic─â ╚Öi financiar─â a celui cu care se ├«ncheie asocierea, obiectul asocierii, modul de delimitare a drepturilor ╚Öi obliga╚¢iilor asocia╚¢ilor;...o)organism de verificare independent─â - entitate care nu se afl─â sub controlul direct sau indirect al concesionarului perimetrului eolian offshore ╚Öi care execut─â o evaluare sau o validare a documenta╚¢iei tehnice aferente proiectului de construire a centralei electrice eoliene offshore, at├ót pentru faza de proiectare, c├ót ╚Öi pentru faza de execu╚¢ie;...p)perimetru eolian offshore - suprafa╚¢─â a domeniului public, corespunz─âtoare proiec╚¢iei plane la suprafa╚¢a m─ârii a conturului p─âr╚¢ii din scoar╚¢a terestr─â, delimitat─â prin raportare la planul de amenajare a spa╚¢iului maritim, care face obiectul concesion─ârii ├«n vederea explor─ârii, construirii ╚Öi exploat─ârii centralelor electrice eoliene offshore, precum ╚Öi a evacu─ârii energiei electrice produse;...q)planul de dezvoltare a RET - planul de dezvoltare a re╚¢elei electrice de transport pe 10 ani, elaborat de c─âtre operatorul de transport ╚Öi sistem, cu respectarea prevederilor art. 35 din Legea nr. 123/2012, cu modific─ârile ╚Öi complet─ârile ulterioare;...r)punctul de racordare la re╚¢eaua de transport a centralei electrice eoliene offshore - punct fizic din re╚¢eaua electric─â de transport, consider├ónd lucr─ârile prev─âzute ├«n planul de dezvoltare al operatorului de transport ╚Öi sistem, cu dat─â de punere ├«n func╚¢iune anterioar─â datei de punere ├«n func╚¢iune a centralei electrice eoliene offshore, la care se racordeaz─â centrala eolian─â offshore;...s)redeven╚¢─â - suma datorat─â de c─âtre titularii contractelor de concesiune pentru concesionarea unui perimetru eolian offshore, pe perioada de exploatare a centralei electrice eoliene offshore;...╚Ö)sistem de ├«nalt─â tensiune ├«n curent continuu, denumit ├«n continuare HVDC - sistem electric care transfer─â energie sub form─â de curent continuu (CC) de ├«nalt─â tensiune ├«ntre dou─â sau mai multe borne colectoare de curent alternativ (CA) ╚Öi cuprinde cel pu╚¢in dou─â sta╚¢ii de conversie cu cabluri sau linii de transport al curentului continuu ├«ntre sta╚¢iile de conversie;...t)sta╚¢ie de conversie offshore - are ├«n╚¢elesul defini╚¢iei prev─âzute la art. 2 pct. 4 din Regulamentul (UE) 2016/1.447 al Comisiei din 26 august 2016 de instituire a unui cod de re╚¢ea privind cerin╚¢ele pentru racordarea la re╚¢ea a sistemelor de ├«nalt─â tensiune ├«n curent continuu ╚Öi a modulelor generatoare din central─â conectate ├«n curent continuu, ├«nsemn├ónd o parte a unui sistem HVDC care cuprinde una sau mai multe unit─â╚¢i de conversie HVDC instalate ├«ntr-un singur loc ├«mpreun─â cu cl─âdiri, bobine de reactan╚¢─â, filtre, dispozitive de putere reactiv─â ╚Öi echipamente de reglaj, monitorizare, m─âsurare, protec╚¢ie, precum ╚Öi echipamente auxiliare;...╚¢)turbin─â eolian─â offshore - echipament care transform─â energia cinetic─â a v├óntului ├«n energie mecanic─â, care, la r├óndul ei, este transformat─â ├«n energie electric─â;...u)zone interzise - zone ├«n care nu pot fi amplasate perimetre pentru construirea centralelor electrice eoliene offshore ╚Öi exploatarea acestora....', NULL, 2, '2025-11-14 21:57:26.235791', '2025-11-14 21:57:26.235791', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1345, 2, '23', 'Articolul 23', NULL, NULL, NULL, 'Aspecte specifice concesion─ârii perimetrelor eoliene offshore, precum ╚Öi explor─ârii, construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, 'Ob╚¢inerea aprob─ârii de dezvoltare, a autoriza╚¢iei de ├«nfiin╚¢are  ╚Öi a licen╚¢ei de producere, ├«n vederea construirii ╚Öi exploat─ârii centralei electrice eoliene offshore', NULL, NULL, '(1)...Prin derogare de la prevederile Legii nr. 422/2001 privind protejarea monumentelor istorice, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare, de la prevederile Ordonan╚¢ei Guvernului nr. 43/2000 privind protec╚¢ia patrimoniului arheologic ╚Öi declararea unor situri arheologice ca zone de interes na╚¢ional, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare, ╚Öi de la prevederile cap. VI din Legea nr. 182/2000 privind protejarea patrimoniului cultural na╚¢ional mobil, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare, construc╚¢ia centralelor electrice eoliene offshore ╚Öi operarea acestora se desf─â╚Öoar─â cu respectarea urm─âtoarelor condi╚¢ii:a)desc─ârcarea de sarcina arheologic─â a siturilor arheologice incluse ├«n lista monumentelor istorice din perimetrul eolian offshore se realizeaz─â ├«n baza opera╚¢iunii de diagnostic arheologic, efectuate prin metode nonintruzive de investigare acustic─â cu sonarul cu scanare lateral─â - side scan sonar - sau cu sonarul multifascicul - multi-beam sonar - care va oferi imagini ale fundului m─ârii ╚Öi cu profilatorul seismo-acustic - sub-bottom profiler - care va oferi imagini 2D p├ón─â la o ad├óncime de minimum 3 metri ├«n subsolul fundului m─ârii; ├«n func╚¢ie de condi╚¢iile amplasamentului, pot fi folosite ╚Öi alte metode;...b)raportul de diagnostic arheologic se ├«ntocme╚Öte de c─âtre institu╚¢iile organizatoare ale cercet─ârilor arheologice prin personal de specialitate atestat ╚Öi ├«nregistrat ├«n registrul arheologilor;...c)desc─ârcarea de sarcina arheologic─â se realizeaz─â numai pentru zona afectat─â de lucr─ârile offshore ╚Öi numai ├«n situa╚¢ia ├«n care raportul de diagnostic arheologic nu a relevat vestigii arheologice subacvatice sau descoperiri arheologice;...d)declasarea total─â sau par╚¢ial─â a siturilor arheologice incluse pe lista monumentelor istorice se realizeaz─â conform normelor ├«n vigoare, aprobate de c─âtre Ministerul Culturii;...e)Ministerul Culturii emite ordinul privind declasarea monumentelor istorice ├«n termen de maximum 60 de zile lucr─âtoare de la depunerea documenta╚¢iei complete;...f)dup─â finalizarea procedurii de desc─ârcare de sarcin─â arheologic─â de c─âtre serviciile deconcentrate ale Ministerului Culturii, Institutul Na╚¢ional al Patrimoniului asigur─â radierea din Repertoriul Arheologic Na╚¢ional a por╚¢iunii din situl arheologic;...g)situl arheologic cu privire la care s-a emis certificatul de desc─ârcare de sarcin─â arheologic─â nu reprezint─â zona cu patrimoniu arheologic ├«n sensul art. 2 alin. (9)-(11) din Ordonan╚¢a Guvernului nr. 43/2000, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare, ╚Öi nu este asimilat unei zone de protec╚¢ie a monumentelor istorice sau a siturilor arheologice ├«n sensul Legii nr. 422/2001, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare, ╚Öi ├«n sensul Ordonan╚¢ei Guvernului nr. 43/2000, republicat─â, cu modific─ârile ╚Öi complet─ârile ulterioare;...h)├«n cazul ├«n care raportul de diagnostic arheologic a relevat vestigii arheologice subacvatice sau descoperiri arheologice, titularul are obliga╚¢ia de a delimita o zon─â de protec╚¢ie de 50 de metri ├«n jurul acestora ╚Öi de a transmite aceste date serviciilor publice deconcentrate ale Ministerului Culturii ├«n termen de 72 de ore de la finalizarea opera╚¢iunii de delimitare;...i)dup─â comunicarea coordonatelor zonei delimitate potrivit dispozi╚¢iilorlit. h), titularul poate continua lucr─ârile offshore ├«n orice alte zone ├«n care nu au fost identificate vestigii arheologice subacvatice conform diagnosticului arheologic prin modificarea corespunz─âtoare a traseului lucr─ârilor offshore ╚Öi a aprob─ârii de dezvoltare;...j)dac─â prin lucr─ârile offshore se identific─â o descoperire arheologic─â ├«nt├ómpl─âtoare, titularul are obliga╚¢ia de a delimita zona cu patrimoniu arheologic eviden╚¢iat ├«nt├ómpl─âtor ╚Öi o zon─â de protec╚¢ie de 50 de metri ├«n jurul acesteia ╚Öi de a transmite aceste date serviciilor publice deconcentrate ale Ministerului Culturii ├«n termen de 72 de ore de la finalizarea opera╚¢iunii de delimitare;...k)titularul are dreptul de a continua lucr─ârile offshore ├«n orice alte zone ├«n care nu au fost identificate vestigii arheologice subacvatice conform diagnosticului arheologic prin modificarea corespunz─âtoare a traseului lucr─ârilor offshore ╚Öi a aprob─ârii de dezvoltare.......(2)...Pentru certificarea calit─â╚¢ii datelor ob╚¢inute ├«n urma m─âsur─âtorilor prev─âzute laalin. (1) lit. a), institu╚¢iile organizatoare vor solicita Direc╚¢iei hidrografice maritime un referat de specialitate privind calitatea colect─ârii, edit─ârii ╚Öi prelucr─ârii finale a datelor hidrografice ╚Öi oceanografice cu respectarea standardelor interna╚¢ionale ├«n domeniu.', NULL, 23, '2025-11-14 21:57:26.235791', '2025-11-14 21:57:26.235791', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1532, 5, '157', 'Articolul 157', NULL, 'Gazele naturale', NULL, 'Prevederi generale privind gazul petrolier lichefiat (GPL), gazul natural comprimat pentru vehicule (GNCV) ╚Öi gazul natural lichefiat (GNL)', NULL, NULL, NULL, NULL, 'Regimul de autorizare ├«n domeniul GNL, GNCV ╚Öi GPL...Operatorii economici ╚Öi/sau persoanele fizice care desf─â╚Öoar─â activit─â╚¢i de proiectare, execu╚¢ie ╚Öi exploatare ├«n domeniul GNL, GNCV, GPL trebuie s─â de╚¢in─â autoriza╚¢ii/licen╚¢e emise de ANRE ├«n baza unor regulamente specifice.', NULL, 157, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1355, 2, '33', 'Articolul 33', NULL, NULL, NULL, 'Limit─âri ale dreptului de proprietate privat─â  ├«n vederea construirii ╚Öi exploat─ârii  centralei electrice eoliene offshore', NULL, NULL, NULL, NULL, '(1)...Limit─ârile prev─âzute laart. 32 alin. (3)se exercit─â cu condi╚¢ia pl─â╚¢ii ├«n prealabil a unei juste ╚Öi echitabile desp─âgubiri ╚Öi a indemniza╚¢iilor corespunz─âtoare, stabilite prin acordul p─âr╚¢ilor....(2)...├Än ipoteza lipsei acordului p─âr╚¢ilor cu privire la justa ╚Öi prealabila desp─âgubire ori cu privire la indemniza╚¢ie, acestea se pl─âtesc persoanei ├«ndrept─â╚¢ite, ├«n cuantumul stabilit prin hot─âr├óre judec─âtoreasc─â.', NULL, 33, '2025-11-14 21:57:26.235791', '2025-11-14 21:57:26.235791', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1362, 2, '40', 'Articolul 40', NULL, NULL, NULL, 'Taxe ╚Öi redeven╚¢e', NULL, NULL, NULL, NULL, 'Plata taxelor, a tarifelor ╚Öi a redeven╚¢elor prev─âzute ├«n prezenta lege pentru concesionarul perimetrului eolian offshore constituie o obliga╚¢ie distinct─â de restul obliga╚¢iilor fiscale.', NULL, 40, '2025-11-14 21:57:26.235791', '2025-11-14 21:57:26.235791', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1542, 5, '167', 'Articolul 167', NULL, 'Gazele naturale', NULL, 'Noua infrastructur─â', NULL, NULL, NULL, NULL, 'Condi╚¢ii de acordare a derog─ârii...La luarea deciziei de acordare a unei derog─âri se ia ├«n considerare, dup─â caz, necesitatea de a impune condi╚¢ii referitoare la durata derog─ârii ╚Öi accesul nediscriminatoriu la conducta de interconectare.', NULL, 167, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1543, 5, '168', 'Articolul 168', NULL, 'Gazele naturale', NULL, 'Noua infrastructur─â', NULL, NULL, NULL, NULL, 'Condi╚¢ii de acordare a derog─ârii ├«n cazul unei conducte de interconectare...La luarea deciziei privind condi╚¢iile prev─âzute la art. 170 se vor avea ├«n vedere, ├«n principal, durata contractelor, capacitatea suplimentar─â ce va fi construit─â sau modificarea capacit─â╚¢ii existente, orizontul de timp al proiectului ╚Öi condi╚¢iile na╚¢ionale.', NULL, 168, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
INSERT INTO legislatie.articole (id, act_id, articol_nr, articol_label, titlu_nr, titlu_denumire, capitol_nr, capitol_denumire, sectiune_nr, sectiune_denumire, subsectiune_nr, subsectiune_denumire, text_articol, explicatie, ordine, created_at, updated_at, ai_status, ai_processed_at, ai_error, metadate, issue_monitoring_id) VALUES (1544, 5, '169', 'Articolul 169', NULL, 'Gazele naturale', NULL, 'Noua infrastructur─â', NULL, NULL, NULL, NULL, 'Reguli ╚Öi mecanisme pentru gestionarea ╚Öi alocarea capacit─â╚¢ii...La acordarea unei derog─âri, ANRE poate decide asupra regulilor ╚Öi mecanismelor pentru gestionarea ╚Öi alocarea capacit─â╚¢ii, ├«n cazul ├«n care acestea nu ├«mpiedic─â punerea ├«n aplicare a contractelor pe termen lung.', NULL, 169, '2025-11-14 21:57:26.33105', '2025-11-14 21:57:26.33105', 'pending', NULL, NULL, NULL, NULL) ON CONFLICT DO NOTHING;
